import React from "react";
// { Fragment, useContext } 
// import Axios from "axios";
// import { useNavigate } from "react-router-dom";
// import { AuthContext } from "../../Helpers/AuthContext";

import { Modal, Col, Form } from "react-bootstrap";
// import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

// //Toast
// import { toast } from "react-toastify";

import Heading from "../Heading";

function JobStatus(props) {

    const grabID = props.grabID;
    const statusCode = props.status;

    let statusInfo;
    let status;
    switch (statusCode) {
      case "0":
        status = "Pending"
        statusInfo = "Pending";
        break;
      case "1":
        status = "Reject"
        statusInfo = "Rejecting";
        break;
      case "2":
        status = "Hired"
        statusInfo = "Hiring";
        break;
        case "3":
          status = "Shortlisted"
        statusInfo = "Shortlisting";
        break;
      default:
        statusInfo = "Pending";
    }


    const aboutClasses ="form-control form-control-sm";
    //  aboutInputHasError
    // ? "form-control form-control-sm is-invalid"
    // : "form-control form-control-sm";

    return (
        <div>
            <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
        {
          status === "Reject" ?
          <Heading content="Reason for Rejection" design="h5" />
          :
          <Heading content={`${statusInfo} this candidate?`} design="h5" />
        }

        </Modal.Header>
        <Modal.Body>
          {status === "Reject" && (
            <>
            <Form >
<Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputabout" className="required">
                    Would appreciate if you could share some feedback about this candidate
                    </label>

                    <Form.Control
                      as="textarea"
                      placeholder="Share some feedback here"
                      className={aboutClasses}
                      // onChange={aboutChangeHandler}
                      // onBlur={aboutBlurHandler}
                      // value={enteredAbout}
                    />
                    {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>
                  <Col sm={12} className="form-group mb-2">

                  </Col>
                  </Form >
            </>
          )}
        </Modal.Body>
        </Modal>
        </div>
    )
}

export default JobStatus
