import React, { useEffect, useState } from "react";
import Axios from "axios";

import { Row, Col, Nav } from "react-bootstrap";
import { BiChevronLeft, BiChevronsRight } from "react-icons/bi";
import briefcaseIcon from "../../Assests/Images/icon/briefcase.svg";

import Heading from "../Heading";
import ApplicationCard from "./ApplicationCard";

function EmployerApplication({ setshowSubContentClick }) {
  const [pendingNum, setPendingNum] = useState(0);
  const [shortlistedNum, setShortlistedNum] = useState(0);
  const [hiredNum, setHiredNum] = useState(0);
  const [rejectNum, setRejectNum] = useState(0);
  const [withdrewNum, setWithdrewNum] = useState(0);
  const [allApplicationNum, setAllApplicationNum] = useState(0);
  const [applicationList, setApplicationList] = useState([]);

  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/dashboard/applicationNum`,
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }
    ).then((response) => {
      setPendingNum(response.data[0][0].pendingNum);
      setRejectNum(response.data[1][0].rejectNum);
      setHiredNum(response.data[2][0].hiredNum);
      setShortlistedNum(response.data[3][0].shortlistedNum);
      setWithdrewNum(response.data[4][0].withdrewNum);
      setAllApplicationNum(response.data[5][0].allApplicationNum);
    });
  }, []);

  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/dashboard/applicationStatusList`,
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }
    ).then((response) => {
      setApplicationList(response.data);
    });
  }, []);

  return (
    <div>
      <h1
        onClick={() => setshowSubContentClick(0)}
        className="me-2 d-block d-sm-none"
      >
        <BiChevronLeft className="d-inline" />
        <Heading content="Application History" design="h1 d-inline-block" />
      </h1>
      <Col lg={10} md={12} sm={12} className="mx-auto my-3">
        <div className="d-none d-lg-block">
          <Nav justify variant="tabs" defaultActiveKey="/home">
            <Nav.Item>
              <Nav.Link eventKey="link-0">All<span>({allApplicationNum})</span></Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-1">Pending<span>({pendingNum})</span></Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-2">Shortlisted<span>({shortlistedNum})</span></Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-3">Hired<span>({hiredNum})</span></Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-4">Rejected<span>({rejectNum})</span></Nav.Link>
            </Nav.Item>
            <Nav.Item>
              <Nav.Link eventKey="link-5">Withdrew<span>({withdrewNum})</span></Nav.Link>
            </Nav.Item>
          </Nav>
        </div>
        <div className="d-block d-md-block d-lg-none">
          <button
            className="ms-2 btn btn-white text-dark border border-1 rounded"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#collapseTabs"
            aria-expanded="false"
            aria-controls="collapseTabs"
          >
            <i className="bi bi-list"></i> Filter
          </button>
          <div className="ms-2 collapse navbar-collapse" id="collapseTabs">
            <ul className="nav navbar-nav dropdown-menu px-3" id="myAppTab">
              <li className="nav-item">
                <div
                  className="nav-link text-dark"
                  id="appAppliedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appApplied"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  All <span>(20)</span>
                </div>
              </li>
              <li className="nav-item">
                <div
                  className="nav-link active text-dark"
                  id="appPendingTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appPending"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Pending <span>(15)</span>
                </div>
              </li>
              <li className="nav-item">
                <div
                  className="nav-link text-dark"
                  id="appShortlistedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appShortlisted"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Shortlisted <span>(1)</span>
                </div>
              </li>
              <li className="nav-item">
                <div
                  className="nav-link text-dark"
                  id="appHiredTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appHired"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Hired <span>(1)</span>
                </div>
              </li>
              <li className="nav-item">
                <div
                  className="nav-link text-dark"
                  id="appRejectedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appRejected"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Rejected <span>(5)</span>
                </div>
              </li>
              <li className="nav-item">
                <div
                  className="nav-link text-dark"
                  id="appWithdrewTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appWithdrew"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Withdrew <span>(1)</span>
                </div>
              </li>
            </ul>
          </div>
        </div>
      </Col>

      <Col lg={10} md={12} sm={12} className="mx-auto my-3">

      {applicationList
      .map((applicationDetails, key) => {
          const grabID = applicationDetails.grab_oppo_id;
          const jobTitle = applicationDetails.job_title;
          const jobType= applicationDetails.job_type;
          const candidateID= applicationDetails.candidate_user_id;

          return (
            <ApplicationCard 
                key={grabID}
                jobTitle={jobTitle}
                jobType={jobType}
                candidateID={candidateID}
            />
          )
      })}
      </Col>
    </div>
  );
}

export default EmployerApplication;
