import React, { Fragment, useEffect, useState } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { useHistory } from "react-router-dom";
import Axios from "axios";

//SESSION and INPUT
import { AuthContext } from "../Helpers/AuthContext";
import useInput from "../Helpers/use-input";

import { Container, Row, Col, InputGroup, Form } from "react-bootstrap";

//Components
import Heading from "../Components/Heading";


const isNotEmpty = (value) => value.trim() !== "";



function BasicInfo() {
  let history = useHistory();

  const [authState, setAuthState] = useState({
    id: "",
    admin: "",
    company: "",
    status: false,
    attempt: "",
  });

  useEffect(() => {
    Axios.get("http://localhost:3001/auth/authenticate", {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      if (response.data.error) {
        setAuthState({ ...authState, status: false });
        history.push("/");
      } else {
        setAuthState({
          id: response.data.id,
          admin: response.data.admin,
          company: response.data.company,
          attempt: response.data.attempt,
          status: true,
        });
      }
    });
  }, []);

    // Gender Input Check
    const {
      value: enteredGender,
      isValid: enteredGenderIsValid,
      hasError: genderInputHasError,
      valueChangeHandler: genderChangeHandler,
      inputBlurHandler: genderBlurHandler,
      reset: resetGenderInput,
    } = useInput(isNotEmpty);

    //DOB Input Check
    const {
      value: enteredDob,
      isValid: enteredDobIsValid,
      hasError: dobInputHasError,
      valueChangeHandler: dobChangeHandler,
      inputBlurHandler: dobBlurHandler,
      reset: resetDobInput,
    } = useInput(isNotEmpty);

  let userBasicInfoFormIsValid = false;

  if ( enteredGenderIsValid && enteredDobIsValid) {
    userBasicInfoFormIsValid = true;
  }
  const userBasicInfoFormSubmissionHandler = (event) => {
    event.preventDefault();

    if ( !enteredGenderIsValid && !enteredDobIsValid) {
      return;
    } else {
      alert(enteredGender);
    }
    resetGenderInput();
    resetDobInput();

  };

  const dobClasses = dobInputHasError
    ? "form-control form-control-sm is-invalid"
    : "form-control form-control-sm";
  return (
    <Fragment>
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <HelmetProvider>
          <Helmet>
            <title> Basic Info | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
        <div id="basicInfo" className="basicInfo">
          <Container className="my-5">
            <Row>
            <Col sm={6} xs={12} className="mx-auto">
      <h1>{authState.id}</h1>
      <Heading content="Basic Info" />
      <Form onSubmit={userBasicInfoFormSubmissionHandler}>
        <Col sm={12} className="form-group mb-3">
          <label htmlFor="inputgender" className="required">
            You are
          </label>
          <InputGroup>
            <div>
              <input
                type="radio"
                className="btn-check"
                name="gender"
                id="male"
                required
                autoComplete="off"
                onChange={genderChangeHandler}
                onBlur={genderBlurHandler}
                value="Male"
              />
              <label className="btn btn-outline-success" htmlFor="male">
                Male
              </label>
            </div>

            <div className="ms-2 ">
              <input
                type="radio"
                className="btn-check"
                name="gender"
                id="female"
                autoComplete="off"
                onChange={genderChangeHandler}
                onBlur={genderBlurHandler}
                value={[enteredGender || "Female"]}
              />
              <label className="btn btn-outline-info" htmlFor="female">
                Female
              </label>
            </div>
          </InputGroup>
          {genderInputHasError && (
            <Form.Control.Feedback type="invalid">
              Please choose your gender.
            </Form.Control.Feedback>
          )}
        </Col>
        <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputdob" className="required">
                  Date of Birth
                </label>
                <input
                  type="date"
                  id="inputdob"
                  placeholder="Your Birthday"
                  className={dobClasses}
                  onChange={dobChangeHandler}
                  onBlur={dobBlurHandler}
                  value={enteredDob}
                />
                {dobInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please select your birthdate.
                  </Form.Control.Feedback>
                )}
              </Col>

        <Col className="text-end my-3">
          <button
            type="submit"
            className=" btn btn-primary"
            disabled={!userBasicInfoFormIsValid}
          >
            Submit
          </button>
        </Col>
      </Form>
    </Col>
            </Row>

          </Container>
        </div>
      </AuthContext.Provider>
    </Fragment>
  );
}

export default BasicInfo;
