import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle, BiBookReader, BiCalendar, BiPlus, BiTrash } from "react-icons/bi";
import { FaUniversity } from "react-icons/fa";


import { Col, Row, Modal, Form, Button } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function Education() {
  const navigate = useNavigate();
  const [show, setShow] = useState(false);

  const [education, setEducation] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/education/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
            setEducation(response.data);
          }

      }
    );
  }
  }, []);

  const aboutMeFormSubmissionHandler =() => {

  }

    return (
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="Education" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto" onClick={() => setShow(true)}><BiPlus className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col>
          {education.map((educationDetails, key) => {
                const school = educationDetails.school;
                const higherLevel = educationDetails.higher_stud;
                const studyField = educationDetails.study_field;
                const grade = educationDetails.grade;
                const gradYearFrom = educationDetails.grad_year_from;
                const gradYearTo = educationDetails.grad_year_to;

                return (
                  <>
                    <div className="mb-2">
                    <Row>
                <Col lg={10} sm={10}>
                  <p className="text-start m-0 fw-bold fs-6">
                    {higherLevel} 
                    {studyField !== null && <> in {studyField}</> }
                  </p>
                </Col>
                <Col lg={2} sm={2} className="text-end">
                  <BiPencil className="icon" />
                  <BiTrash className="icon" />
                </Col>
              </Row>
              <Row>
                <p className="m-0 text-start fs-7">
                  <FaUniversity className="icon" />
                  {school}
                </p>
                {grade !== null &&
                <p className="m-0 text-start fs-7">
                  <BiBookReader className="icon" />
                  {grade}
                </p>
                }
                <p  className="m-0 text-start fs-7"><BiCalendar className="icon"/> {gradYearFrom} - {gradYearTo}</p>
              </Row>
              </div>

              <Modal
                    size="md"
                    scrollable
                    show={show}
                    onHide={() => setShow(false)}
                  >
                    <Modal.Header closeButton>
                      <Modal.Title>
                        <Heading content="About Me" design="h4 my-auto" />
                      </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                      <Form onSubmit={aboutMeFormSubmissionHandler}>
                      njni
                      </Form>
                    </Modal.Body>
                    <Modal.Footer>
                      <Button
                        variant="secondary"
                        onClick={() => setShow(false)}
                      >
                        Close
                      </Button>
                      <Button type="submit" variant="primary" >
                        Save Changes
                      </Button>
                    </Modal.Footer>
                  </Modal>


              </>

                )
            })}
          </Col>
        </Col>
      </Row>
    )
}

export default Education
