import React, { Fragment, useState } from "react";
import { useHistory } from "react-router-dom";
import Axios from "axios";

import { Card, Container, Row, Col, Button, Collapse } from "react-bootstrap";

//Icon FcLike
import {
  FcMoneyTransfer,
  FcSurvey,
  FcReadingEbook,
  FcManager,
  FcLikePlaceholder,
} from "react-icons/fc";
import { BiChevronsDown, BiMessageCheck } from "react-icons/bi";
import { GiBrain, GiVideoConference } from "react-icons/gi";
import { FaUniversity } from "react-icons/fa";

//Toast
import { toast } from "react-toastify";

function OpportunityLongCard(props) {
  let history = useHistory();

  const [open, setOpen] = useState(false);

  function copyLink(link) {
    navigator.clipboard.writeText(link);
    return toast.dark(
      <Fragment>
        <BiMessageCheck />
        <span>Copied to the clipboard!</span>
      </Fragment>
    );
  }

  let jobTypeBG;
  switch (props.jobType) {
    case "Contract":
      jobTypeBG = "bg-contract";
      break;
    case "Internship":
      jobTypeBG = "bg-internship";
      break;
    case "Part Time":
      jobTypeBG = "bg-partTime";
      break;
    default:
      jobTypeBG = "bg-fullTime";
  }

  var oppoLink =
    "/opportunity/" +
    props.compName
      .replace(/\//g, "-")
      .toLowerCase()
      .trim()
      .split(/\s+/)
      .join("-") +
    "/" +
    props.jobTitle
      .replace(/\//g, "-")
      .toLowerCase()
      .trim()
      .split(/\s+/)
      .join("-") +
    "/" +
    props.oppoID;
  var shareLink = "https://www.zom-in.com/" + oppoLink;

  const applyJob = (event) => {
   console.log(img);
   console.log(vid);
  };

  const saveJob = (event) => {
    Axios.post(
      `${process.env.REACT_APP_SERVER_DOMAIN}/opportunity/saveJob`,
      {
        gender: "male",
      },
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }
    ).then((response) => {
      if (response.data.error) {
        if (
          response.data.error.message === "jwt malformed" ||
          response.data.error.message === "Cannot Access"
        ) {
          alert("plasea log in");
          console.log(response.data.error);
          history.push("/signup");
        } else {
          alert("system issue kot");
        }
      } else {
        alert("what?");
      }
    });
  };
  return (
    <Card className="cardOppo cardLOppo position-relative">
      <Container>
        <Row className="mb-1 border-bottom">
          <Col xs={3} sm={3} md={2} lg={2}>
            <img
              className="imgOppo float-start"
              alt="zom-in partner logo"
              src={props.cardImage}
            />
          </Col>
          <Col xs={9} sm={9} md={7} lg={7} className="text-start p-1">
            <Row>
              <Col className="lh-1">
                <dt className="h5 text-truncate">
                  <a
                    className="link-dark text-decoration-none "
                    href={oppoLink}
                    rel="noreferrer"
                    target="_blank"
                  >
                    {props.jobTitle}
                  </a>
                </dt>
                <a
                  href={props.compLink}
                  className="text-decoration-none text-truncate"
                >
                  {props.compName}
                </a>
                <div className="d-flex flex-wrap align-items-center text-start cardJobDetail mb-4">
                  <p className="text-truncate">
                    <span
                      className={`badge rounded-pill text-dark ${jobTypeBG}`}
                    >
                      <img
                        alt="dg"
                        className="icon"
                        src="https://www.zom-in.com/images/icon/opportunity.svg"
                      />
                      {props.jobType}
                    </span>
                  </p>

                  {props.salaryStatus === 1 ? (
                    <p className="text-truncate">
                      <FcMoneyTransfer />
                      {props.salary}
                    </p>
                  ) : (
                    <p className="text-truncate text-muted">
                      <FcMoneyTransfer />
                      Undisclosed
                    </p>
                  )}

                  {props.nationality !== "Not specific" ? (
                    <p className="text-truncate">
                      <FcManager />
                      {props.nationality}
                    </p>
                  ) : null}

                  {props.jobTest === 1 ? (
                    <p className="text-truncate">
                      <FcSurvey /> Test / Assessment will be conducted
                    </p>
                  ) : null}
                  <p className="text-truncate">
                    <img
                      alt="dg"
                      className="icon"
                      src="https://www.zom-in.com/images/img/location.svg"
                    />{" "}
                    {props.jobLocation}
                  </p>
                </div>
                <Collapse in={open} className="pt-1">
                  <Col
                    xs={9}
                    sm={10}
                    md={12}
                    lg={12}
                    id="collapseID"
                    className="text-start p-1"
                  >
                    <p className="h6">Job Details</p>
                    <div className="fs-6 p-2">
                      {props.yearExp > 0 && (
                        <p className="text-truncate">
                          <GiBrain className="icon" /> {props.yearExp} years of
                          experience
                        </p>
                      )}

                      {props.freshGraduate === 1 && (
                        <p className="text-truncate">
                          <img
                            alt="zom-in-fresh-graduate-icon"
                            className="icon"
                            src="https://www.zom-in.com/images/icon/graduated.svg"
                          />
                          Open for Fresh Graduates
                        </p>
                      )}

                      {props.education && (
                        <p className="text-truncate">
                          <FaUniversity className="icon" /> {props.education}
                        </p>
                      )}

                      {props.fieldStudy && (
                        <p className="text-truncate">
                          <FcReadingEbook className="icon" /> {props.fieldStudy}
                        </p>
                      )}

                      {props.ivOption && (
                        <p className="text-truncate">
                          <GiVideoConference className="icon" />{" "}
                          {props.ivOption}
                        </p>
                      )}
                    </div>
                    <p className="h6">Job Requirement / Responsibilities</p>
                    <div className="fs-6 p-2">
                      <div
                        dangerouslySetInnerHTML={{ __html: props.jobDesc }}
                      />
                    </div>
                  </Col>
                </Collapse>
              </Col>
            </Row>
          </Col>
        </Row>
        <Row className="mt-1">
          <Col xs={4} sm={4} md={4} lg={4} className="d-grid gap-2">
            <Button
              variant="outline-secondary border-0"
              size="sm"
              onClick={() => copyLink(shareLink)}
            >
              {" "}
              Share
            </Button>
          </Col>
          <Col
            xs={4}
            sm={4}
            md={4}
            lg={4}
            className="border-start border-end d-grid gap-2"
          >
            <Button
              onClick={() => setOpen(!open)}
              aria-expanded={open}
              aria-controls="collapseID"
              variant="outline-info border-0"
              size="sm"
            >
              {" "}
              <BiChevronsDown />
              View More
            </Button>
          </Col>
          <Col xs={4} sm={4} md={4} lg={4} className="d-grid gap-2">
            <Button
              variant="outline-success border-0"
              size="sm"
              onClick={() =>
                              applyJob(
                                props.jobTitle,
                                props.compName
                              )
                            }
            >
              {" "}
              Apply Now
            </Button>
          </Col>
        </Row>
      </Container>

      <div
        className="position-absolute top-0 end-0 mt-2 me-2"
        onClick={saveJob}
      >
        <FcLikePlaceholder size={28} className="saveBtn" />
        {/* <FcLike 
                    className="unSaveBtn"
                /> */}
      </div>
    </Card>
  );
}

export default OpportunityLongCard;
