import React, { useState, useEffect, Fragment } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import useInput from "../../../../Helpers/use-input";
import {
  BiPencil,
  BiMinusCircle,
  BiBookReader,
  BiCalendar,
  BiPlus,
  BiTrash,
} from "react-icons/bi";
import { FaUniversity } from "react-icons/fa";
import { Typeahead } from "react-bootstrap-typeahead";
import DatePicker from "react-datepicker";

import { Col, Row, Modal, Form, Button, InputGroup } from "react-bootstrap";
import Heading from "../../../Heading";
import { toast } from "react-toastify";

const isNotEmpty = (value) => value.trim() !== "";

function Education() {
  const navigate = useNavigate();
  const [showAdd, setShowAdd] = useState(false);

  const [eduList, setEduList] = useState([]);
  const [universityList, setUniversityList] = useState([]);
  const [education, setEducation] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/education/`,
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          );
        } else {
          setEducation(response.data);
        }
      });
    }
  }, []);

  //Education Level
  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/meta/eduLevel`).then(
      (response) => {
        setEduList(response.data);
      }
    );
  });

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/meta/university`).then(
      (response) => {
        const uni = response.data;
        var rows = [];
        for (var i = 0; i < uni.length; i++) {
          rows.push(response.data[i].university_name);
        }
        setUniversityList(rows);
      }
    );
});

  //Highest Education Level Input Check
  const {
    value: enteredHighLevel,
    isValid: enteredHighLevelIsValid,
    hasError: highLevelInputHasError,
    valueChangeHandler: highLevelChangeHandler,
    inputBlurHandler: highLevelBlurHandler,
    reset: resetHighLevelInput,
  } = useInput(isNotEmpty);

    //Study Field Level Input Check
    const {
      value: enteredStudyField,
      isValid: enteredStudyFieldIsValid,
      hasError: studyFieldInputHasError,
      valueChangeHandler: studyFieldChangeHandler,
      inputBlurHandler: studyFieldBlurHandler,
      reset: resetStudyFieldInput,
    } = useInput(isNotEmpty);

  const [school, setSchool] = useState("");
    //Start Year Input Check
    const [startDate, setStartDate] = useState();

    //End Year Input Check
    const [endDate, setEndDate] = useState();


  let addEducationFormIsValid = false;

  if (enteredHighLevelIsValid && 
    enteredStudyFieldIsValid &&
    school !== "") {
    addEducationFormIsValid = true;
  }

  const aboutMeFormSubmissionHandler = (event) => {
    event.preventDefault();

    if (!enteredHighLevelIsValid &&
      !enteredStudyFieldIsValid &&
      school === ""  ) {
      return;
    } else {
      alert("senidng");
    }

    resetHighLevelInput();
    resetStudyFieldInput();
    setSchool("");

  };

  const highLevelClasses = highLevelInputHasError
    ? "form-select form-select-sm is-invalid"
    : "form-select form-select-sm";

  const studyFieldClasses = studyFieldInputHasError
    ? "form-control form-control-sm is-invalid"
    : "form-control form-control-sm";

  return (
    <Row className="mx-auto border rounded p-2 my-2">
      <Col sm={12}>
        <Row>
          <Col xs={10} sm={10} className="text-start">
            <Heading content="Education" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p
              role="button"
              className="my-auto"
              onClick={() => setShowAdd(true)}
            >
              <BiPlus className="icon" />
            </p>
          </Col>
        </Row>
      </Col>
      <Col sm={12}>
        <Col>
          {education.map((educationDetails, key) => {
            const school = educationDetails.school;
            const higherLevel = educationDetails.higher_stud;
            const studyField = educationDetails.study_field;
            const grade = educationDetails.grade;
            const gradYearFrom = educationDetails.grad_year_from;
            const gradYearTo = educationDetails.grad_year_to;

            return (
              <>
                <div className="mb-2">
                  <Row>
                    <Col lg={10} sm={10}>
                      <p className="text-start m-0 fw-bold fs-6">
                        {higherLevel}
                        {studyField !== null && <> in {studyField}</>}
                      </p>
                    </Col>
                    <Col lg={2} sm={2} className="text-end">
                      <BiPencil className="icon" />
                      <BiTrash className="icon" />
                    </Col>
                  </Row>
                  <Row>
                    <p className="m-0 text-start fs-7">
                      <FaUniversity className="icon" />
                      {school}
                    </p>
                    {grade !== null && (
                      <p className="m-0 text-start fs-7">
                        <BiBookReader className="icon" />
                        {grade}
                      </p>
                    )}
                    <p className="m-0 text-start fs-7">
                      <BiCalendar className="icon" /> {gradYearFrom} -{" "}
                      {gradYearTo}
                    </p>
                  </Row>
                </div>

                <Modal
                  size="md"
                  scrollable
                  show={showAdd}
                  onHide={() => setShowAdd(false)}
                >
                  <Modal.Header closeButton>
                    <Modal.Title>
                      <Heading content="Add Education" design="h4 my-auto" />
                    </Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <Form onSubmit={aboutMeFormSubmissionHandler}>
                      <Col sm={12} className="form-group mb-2">
                        <label htmlFor="eduLevel" className="required">
                          Highest Education Level
                        </label>
                        <select
                          defaultValue={""}
                          aria-label="Education Level"
                          className={highLevelClasses}
                          onChange={highLevelChangeHandler}
                          onBlur={highLevelBlurHandler}
                        >
                          <option value="">Please Choose One</option>
                          {eduList.map((eduList, key) => {
                            const enteredHighLevel = eduList.child_name;
                            return (
                              <option
                                key={enteredHighLevel}
                                value={enteredHighLevel}
                              >
                                {enteredHighLevel}
                              </option>
                            );
                          })}
                        </select>

                        {highLevelInputHasError && (
                          <Form.Control.Feedback type="invalid">
                            Please choose your highest education Level.
                          </Form.Control.Feedback>
                        )}
                      </Col>

                      <Col sm={12} className="form-group mb-2">
                    <label htmlFor="studyField" className="required">
                      Field of Study
                    </label>
                    <input
                      type="text"
                      className={studyFieldClasses}
                      onChange={studyFieldChangeHandler}
                      onBlur={studyFieldBlurHandler}
                      value={enteredStudyField}
                    />

                    {studyFieldInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your field of study.
                      </Form.Control.Feedback>
                    )}
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputSchool" className="required">
                      School
                    </label>

                    <Typeahead
                      inputProps={{ required: true }}
                      id="valid-styles-example"
                      size="small"
                      placeholder="Choose a School"
                      options={universityList}
                      onChange={(value) => {
                        setSchool(value);
                      }}
                      onInputChange={(value) => {
                        setSchool(value);
                      }}
                    />
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="eduYear" className="required">
                      Year
                    </label>
                    <InputGroup className="mb-3">
                      <Col sm={4} xs={6}>
                        <DatePicker
                          required
                          selected={startDate}
                          onChange={(date) => setStartDate(date)}
                          showYearPicker
                          dateFormat="yyyy"
                          yearItemNumber={10}
                          placeholderText="Year Start"
                        />
                      </Col>
                      <Col sm={4} xs={6} >
                        <DatePicker
                          selected={endDate}
                          onChange={(date) => setEndDate(date)}
                          showYearPicker
                          dateFormat="yyyy"
                          yearItemNumber={10}
                          placeholderText="Year End"
                        />
                      </Col>
                    </InputGroup>
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="grade" className="required">
                      Latest CGPA / Grade
                    </label>
                    <input
                      type="text"
                      className={gradeClasses}
                      onChange={gradeChangeHandler}
                      onBlur={gradeBlurHandler}
                      value={enteredGrade}
                    />

                    {gradeInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your field of study.
                      </Form.Control.Feedback>
                    )}
                  </Col>


                    </Form>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button
                      variant="secondary"
                      onClick={() => setShowAdd(false)}
                    >
                      Close
                    </Button>
                    <Button type="submit" variant="primary">
                      Save Changes
                    </Button>
                  </Modal.Footer>
                </Modal>
              </>
            );
          })}
        </Col>
      </Col>
    </Row>
  );
}

export default Education;
