import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../../Helpers/AuthContext";
import Axios from "axios";

import { Row, Col, Button, Modal } from "react-bootstrap";
import briefcaseIcon from "../../Assests/Images/icon/briefcase.svg";
import { BiTime, BiXCircle, BiCheckCircle, BiMinusCircle } from "react-icons/bi";

import Ads from "../Ads";
import Profile from "../Profile";
import Name from "../Name";

function User() {
  const { authState, setAuthState } = useContext(AuthContext);
  const [applicationList, setApplicationList] = useState([]);
  const [withdrawModal, setWithdrawModal] = useState(false);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/userApplication/list`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      setApplicationList(response.data);
      console.log(response.data);
    });
  }, []);


  const withdrawJob = (id) => {
    let opportunityInfo = [id];
    alert (id)

  }

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <div>
        <Row className="mx-auto p-5">
          <Col lg={10} md={10} sm={12}>
            <ul className="nav nav-fill nav-tabs" id="appTab1" role="tablist">
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link active"
                  id="appAppliedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appApplied"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  All <span>(20)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appPendingTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appPending"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Pending <span>(15)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appAcceptedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appAccepted"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Accepted <span>(1)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appRejectedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appRejected"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Rejected <span>(5)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appWithdrewTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appWithdrew"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Withdrew <span>(2)</span>
                </button>
              </li>
            </ul>

            <div className="tab-content" id="appTabContent">
              <div
                className="tab-pane fade show active"
                id="appApplied"
                role="tabpanel"
                aria-labelledby="applied-tab"
              >
                {applicationList.map((applicationDetails, key) => {
                  const jobTitle = applicationDetails.job_title;
                  const compName = applicationDetails.user_id;
                  const jobType = applicationDetails.job_type;
                  const applicationStatus = applicationDetails.approve_candidate;
                  const grabID = applicationDetails.grab_oppo_id;

                  let jobTypeBG;
                  switch (jobType) {
                    case "Contract":
                      jobTypeBG = "bg-contract";
                      break;
                    case "Internship":
                      jobTypeBG = "bg-internship";
                      break;
                    case "Part Time":
                      jobTypeBG = "bg-partTime";
                      break;
                    default:
                      jobTypeBG = "bg-fullTime";
                  }

                  let status;
                  let statusBG;
                  let statusIcon;
                  switch (applicationStatus) {
                    case 4:
                      status = "Withdrew";
                      statusBG = "link-danger";
                      statusIcon = <BiMinusCircle className="icon" />;
                      break;
                    case 3:
                      status = "Shortlisted";
                      statusBG = "link-warning";
                      statusIcon = <BiTime className="icon" />;
                      break;
                    case 2:
                      status = "Accepted";
                      statusBG = "link-success";
                      statusIcon = <BiCheckCircle className="icon" />;
                      break;
                    case 1:
                      status = "Rejected";
                      statusBG = "link-danger";
                      statusIcon = <BiXCircle className="icon" />;
                      break;
                    default:
                      status = "Pending";
                      statusBG = "link-warning";
                      statusIcon = <BiTime className="icon" />;
                  }

                  return (
                    <>
                    <Row className="mx-auto my-3 p-3">
                      <Col
                        xs={1}
                        sm={2}
                        className="d-none d-md-none d-lg-block"
                      >
                        <Profile
                          id={compName}
                          design="rounded-circle"
                          width="100"
                          height="100"
                        />
                      </Col>
                      <Col xs={8} sm={8}>
                        <p className="m-0">{jobTitle}</p>
                        <p className="m-0">
                          <Name id={compName} />
                        </p>
                        <p>
                          <span
                            className={`badge rounded-pill text-dark ${jobTypeBG}`}
                          >
                            <img
                              alt="briefcaseIcon"
                              className="icon"
                              src={briefcaseIcon}
                            />
                            {jobType}
                          </span>
                        </p>
                      </Col>
                      <Col sm={2} className="text-center">
                        <p className={statusBG}>
                          {" "}
                          {statusIcon} {status}
                        </p>
                        {status === "Shortlisted" || status === "Pending" 
                        ? <Button variant="danger" onClick={() => setWithdrawModal(true)} >Withdraw</Button>
                        : null}
                      </Col>
                    </Row>
                    <Modal
        size="sm"
        show={withdrawModal}
        onHide={() => setWithdrawModal(false)}
      aria-labelledby="contained-modal-title-vcenter"
      centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-sm">
            Small Modal
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>...</Modal.Body>
        <Modal.Footer>
        <Button variant="danger" onClick={() => withdrawJob(grabID) }>Withdraw</Button>
        </Modal.Footer>
      </Modal>
      </>
                  );
                })}
              </div>
            </div>
          </Col>
          <Col lg={2} md={2} sm={12} className="px-1 py-3 my-1 mx-auto">
            <Ads />
          </Col>
        </Row>
      </div>
    </AuthContext.Provider>
  );
}

export default User;
