import React, {useEffect, useState} from 'react';
import Axios from'axios';
import { Col } from "react-bootstrap";

import { FaThumbsUp } from "react-icons/fa";

function LikePost(props) {

    const postID = props.postID;
    const userID = props.userID;

    const [likeList, setLikeList] = useState([]);

  //UserName
  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/like/` + userID
    ).then((response) => {
        setLikeList(response.data);
    });
  }, []);

    return (
        <>
        {likeList.map((likeDetails, key) => {
            const likeOwnerID = likeDetails.user_id;
            const likeStatus = likeDetails.like_post;
            const likePostID = likeDetails.post_id;



            return (
                <>
                {likeStatus === 1 ?
                    <Col
                xs={4}
                sm={4}
                className="text-center link-secondary"
                role="button"
              >
                <FaThumbsUp /> Like
              </Col>
                :
                <Col
                xs={4}
                sm={4}
                className="text-center link-secondary"
                role="button"
              >
                <FaThumbsUp /> Likedd
              </Col>
                 }
                 </>

            )
        })}

              </>
    )
}

export default LikePost
