import React, { useEffect, useContext, useState } from "react";
import Axios from "axios";
import { AuthContext } from "../../Helpers/AuthContext";

import CoverImg from "../../Assests/Images/background/bg-1.png";
import UserImg from "../../Assests/Images/user/sha.jpeg";

function Profile() {
  const { authState, setAuthState } = useContext(AuthContext);

  const [viewName, setViewName] = useState([]);
  const [viewProfile, setViewProfile] = useState([]);
  const [viewGender, setViewGender] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/home/userProfile`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      const profileDetail1 = response.data[0];
      const profileDetail2 = response.data[1];
      const profileDetail3 = response.data[2];
      setViewName(profileDetail1);
      setViewProfile(profileDetail2);
      setViewGender(profileDetail3);

      // if (profileDetail2.length === 0) {
      //   setViewProfile(profileDetail3);
      // } else {
      // setViewProfile(profileDetail2);
      // }
    });
  });

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      {viewName.map((userNameDetail, key) => {
        const userName = userNameDetail.name;

        return (
          <div className="card mb-3">
            <img
              src={CoverImg}
              className="card-img-top profilecover"
              alt="Background Cover"
            />
            <div className="position-relative">
              <div className="position-absolute top-50 start-50 translate-middle w-50">

              {viewProfile !== [] ? 
              <img
                  src={require(`../../Assests/Images/user/sha.jpeg`).default}
                  className="card-img-top rounded-circle"
                  alt="Background Cover"
                /> : 
                <>
                {viewGender.gender === "Male" 
                ?
                <img
                  src={require(`../../Assests/Images/avatar/male.svg`).default}
                  className="card-img-top rounded-circle"
                  alt="Background Cover"
                />
                :
                <img
                  src={require(`../../Assests/Images/avatar/female.svg`).default}
                  className="card-img-top rounded-circle"
                  alt="Background Cover"
                />
                }
                </>
                }
              </div>
            </div>
            <div className="card-body mt-4 text-center pb-0">
              <h6 href="/edcwe" className="card-title pt-3">
                <a className="text-decoration-none link-dark" href="/profile">
                  {userName}
                </a>
              </h6>
              <p className="card-text text-muted text-small">
                Web Developer at Zom-IN
              </p>
            </div>
            <hr className="dropdown-divider" />
            <div className="mx-auto m-1">
              <a href="/profile" className="text-decoration-none link-primary">
                View Profile
              </a>
            </div>
          </div>
        );
      })}
    </AuthContext.Provider>
  );
}

export default Profile;
