import React, { Fragment, useEffect, useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Axios from "axios";
import { Container, Row, Col } from "react-bootstrap";

import Heading from "../Components/Heading";

import { AuthContext } from "../Helpers/AuthContext";

function Dashboard() {
  const navigate = useNavigate();

  const { authState, setAuthState } = useContext(AuthContext);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/basicinfo/status/`, {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }).then((response) => {
        const attemptStatus = response.data[0];
        if (attemptStatus.attempt === 1) {
          navigate("/");
        }
      });
    }
  });

  const [showSubContent, setshowSubContent] = useState(1);
  const subContentStatus = (currentSubContent) => {
    setshowSubContent(currentSubContent);
  };


  let mobileStatus;
  if (window.innerWidth < 426) {
    if (showSubContent > 0) {
        mobileStatus = "d-none";
    } else {
        mobileStatus = "d-block";
    }
  }

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <Fragment>
        <HelmetProvider>
          <Helmet>
            <title> Dashboard | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
      </Fragment>
      <div id="dashboard" className="dashboard my-2">
        <Container>
          <Row>
            <Col lg={2} md={3} sm={12} className={`mb-4 ${mobileStatus}`}>

            {/* MY DASHBOARD */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(1)}
                role="button"
              >
                <SubMenu content="Password" />
              </Row>

            {/* MY PROFILE */}
            <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(1)}
                role="button"
              >
                <SubMenu content="Password" />
              </Row>


              {/* APPLICATION HISTORY */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(1)}
                role="button"
              >
                <SubMenu content="Password" />
              </Row>

              {/* INTERVIEW SCHEDULE */}
              <Row
                className="d-flex justify-content-between border-bottom"
                onClick={(e) => subContentStatus(1)}
                role="button"
              >
                <SubMenu content="Password" />
              </Row>

            </Col>


            <Col lg={8} md={9} sm={12} className={!mobileStatus}>
              {showSubContent === 1 && (
                <Password
                  showSubContent={showSubContent}
                  setshowSubContentClick={setshowSubContent}
                />
              )}
            </Col>
            <Col lg={2} className="d-none d-md-none d-lg-block border">
              <Heading content="Ads" design="h5" />
            </Col>
          </Row>
        </Container>
      </div>
    </AuthContext.Provider>
  );
}

export default Dashboard;
