import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle, BiXCircle, BiMessageCheck  } from "react-icons/bi";

import { Col, Row,  Modal, Button } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function Skills() {
  const navigate = useNavigate();

  const [showDelete, setShowDelete] = useState(false);
  const [skills, setSkills] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/skills/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
            setSkills(response.data);
          }

      }
    );
  }
  }, []);

  const deleteSkill = (skillID) => {
    Axios.delete(
      `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/skills/` + skillID,
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }
    ).then((response) => {
      if (response.data.error) {
        return toast.error(
          <Fragment>
            <BiMinusCircle /> <span>{response.data.error}</span>
          </Fragment>
        );
      } else {
        return [
          toast.success(
            <Fragment>
              <BiMessageCheck /> <span>{response.data.success}</span>
            </Fragment>
          ),
          setSkills(
            skills.filter((skillsDetails) => {
              /* eslint-disable-next-line */
              return skillsDetails.id != skillID;
            })
          ),
          setShowDelete(false)
        ];
      }

    });
  };

    return (
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="Skills" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto"><BiPencil className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col>
          <div className="d-flex flex-wrap">
              {skills.map((skillsDetails, key) => {
                  const skill = skillsDetails.skill;

                  return (
                    <p className="badge rounded-pill alert-primary fw-normal text-dark m-2 fs-7">
                  {skill}
                </p>
                  )
              })}
              </div>
          </Col>
        </Col>
      </Row>
    )
}

export default Skills
