import React, { useState, useContext } from "react";
import Axios from "axios";
import { useHistory } from 'react-router-dom';
import { AuthContext } from '../Helpers/AuthContext';



function Test() {

  let history = useHistory();

    const [emailConf, setEmailConf] = useState("samiksha.zomin@gmail.com");
    const [passwordConf, setPasswordConf] = useState("10@J");

    const {setAuthState } = useContext(AuthContext);

    Axios.defaults.withCredentials = true;
        //LogIn Form
        const login = (event) => {

            if(emailConf && passwordConf) {
                Axios.post("http://localhost:3001/testsession/login", {
                    email: emailConf,
                    password: passwordConf
                })
                .then((response) => {
                    console.log(response.data)
                    if(response.data.error){
                       alert(response.data.error);
                    } else {
                      localStorage.setItem("accessToken", response.data.token);
                      setAuthState({
                        id: response.data.id,
                        admin: response.data.admin,
                        company: response.data.company,
                        status: true,
                      });
                      history.push("/");
                    }




                });
            }
            event.preventDefault();
            setEmailConf("");
            setPasswordConf("");
            console.log("sending");
        };


  return (
    <div >
    <div className="testsed">
      <div>
        <input
          type="email"
          required
          name="email"
          placeholder="Email Address"
          value={emailConf}
          onChange={(e) => {
            setEmailConf(e.target.value);
          }}
        />
      </div>
      <div>
        <input
          type="password"
          required
          name="password"
          placeholder="Password"
          value={passwordConf}
          onChange={(e) => {
            setPasswordConf(e.target.value);
          }}
        />
      </div>

      <div>
      <input type="submit" value="Submit" onClick={login} />
      </div>      
      </div>
    </div>
  );
}

export default Test;
