import React, { Fragment, useEffect, useState, useContext } from "react";
import { AuthContext } from "../../Helpers/AuthContext";
import Axios from "axios";

import { Row, Col, Button, Modal, Form, Alert } from "react-bootstrap";
import briefcaseIcon from "../../Assests/Images/icon/briefcase.svg";
import {
  BiTime,
  BiXCircle,
  BiCheckCircle,
  BiMessageCheck,
  BiMinusCircle,
  BiErrorCircle,
} from "react-icons/bi";
import { toast } from "react-toastify";


import Ads from "../Ads";
import Profile from "../Profile";
import Name from "../Name";
import Heading from "../Heading";

function User() {
  const { authState, setAuthState } = useContext(AuthContext);
  const [applicationList, setApplicationList] = useState([]);
  const [withdrawModal, setWithdrawModal] = useState(false);
  const [requestWithdraw, setRequestWithdraw] = useState("");

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/userApplication/list`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      setApplicationList(response.data);
      console.log(response.data);
    });
  }, []);

  let requestWithdrawFormIsValid = false;

  if (requestWithdraw !== "") {
    requestWithdrawFormIsValid = true;
  }

  const requestWithdrawFormSubmissionHandler = (event) => {
    event.preventDefault();
    const withdrewID = event.target[0].value;

    if (requestWithdraw === "") {
      return;
    } else {
      Axios.patch(
        `${process.env.REACT_APP_SERVER_DOMAIN}/userApplication/withdraw`,
        {
          grabID: withdrewID,
          requestWithdraw: requestWithdraw,
        },
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return [
            toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          ),
          setWithdrawModal(false)
        ];
        } else if (response.data.success) {
          return [
            toast.success(
              <Fragment>
                <BiMessageCheck /> <span>{response.data.success}</span>
              </Fragment>
            ),
            setWithdrawModal(false),
          ];
        } else {
          alert("wrong here");
        }
      });
    }
    setRequestWithdraw("");
  };

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <div>
        <Row className="mx-auto p-5">
          <Col lg={10} md={10} sm={12}>
            <ul className="nav nav-fill nav-tabs" id="appTab1" role="tablist">
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link active"
                  id="appAppliedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appApplied"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  All <span>(20)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appPendingTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appPending"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Pending <span>(15)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appAcceptedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appAccepted"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Accepted <span>(1)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appRejectedTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appRejected"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Rejected <span>(5)</span>
                </button>
              </li>
              <li className="nav-item" role="presentation">
                <button
                  className="nav-link"
                  id="appWithdrewTab"
                  data-bs-toggle="tab"
                  data-bs-target="#appWithdrew"
                  type="button"
                  role="tab"
                  aria-controls="home"
                  aria-selected="true"
                >
                  Withdrew <span>(2)</span>
                </button>
              </li>
            </ul>

            <div className="tab-content" id="appTabContent">
              <div
                className="tab-pane fade show active"
                id="appApplied"
                role="tabpanel"
                aria-labelledby="applied-tab"
              >
                {applicationList.map((applicationDetails, key) => {
                  const jobTitle = applicationDetails.job_title;
                  const compName = applicationDetails.user_id;
                  const jobType = applicationDetails.job_type;
                  const applicationStatus =
                    applicationDetails.approve_candidate;
                  const grabID = applicationDetails.grab_oppo_id;
                  const withdrawReason = applicationDetails.withdrawReason;

                  let jobTypeBG;
                  switch (jobType) {
                    case "Contract":
                      jobTypeBG = "bg-contract";
                      break;
                    case "Internship":
                      jobTypeBG = "bg-internship";
                      break;
                    case "Part Time":
                      jobTypeBG = "bg-partTime";
                      break;
                    default:
                      jobTypeBG = "bg-fullTime";
                  }

                  let status;
                  let statusBG;
                  let statusIcon;
                  switch (applicationStatus) {
                    case 4:
                      status = "Withdrew";
                      statusBG = "link-danger";
                      statusIcon = <BiMinusCircle className="icon" />;
                      break;
                    case 3:
                      status = "Shortlisted";
                      statusBG = "link-warning";
                      statusIcon = <BiTime className="icon" />;
                      break;
                    case 2:
                      status = "Accepted";
                      statusBG = "link-success";
                      statusIcon = <BiCheckCircle className="icon" />;
                      break;
                    case 1:
                      status = "Rejected";
                      statusBG = "link-danger";
                      statusIcon = <BiXCircle className="icon" />;
                      break;
                    default:
                      status = "Pending";
                      statusBG = "link-warning";
                      statusIcon = <BiTime className="icon" />;
                  }

                  return (
                    <>
                      <Row className="mx-auto my-3 p-3">
                        <Col
                          xs={1}
                          sm={2}
                          className="d-none d-md-none d-lg-block"
                        >
                          <Profile
                            id={compName}
                            design="rounded-circle"
                            width="100"
                            height="100"
                          />
                        </Col>
                        <Col xs={8} sm={8}>
                          <p className="m-0">{jobTitle}</p>
                          <p className="m-0">
                            <Name id={compName} />
                          </p>
                          <p>
                            <span
                              className={`badge rounded-pill text-dark ${jobTypeBG}`}
                            >
                              <img
                                alt="briefcaseIcon"
                                className="icon"
                                src={briefcaseIcon}
                              />
                              {jobType}
                            </span>
                          </p>
                          {status === "Withdrew" && <Alert variant="danger">
                          <BiErrorCircle /> Withdrew Reason: {withdrawReason}
                          </Alert>}
                        </Col>
                        <Col sm={2} className="text-center">
                          <p className={statusBG}>
                            {" "}
                            {statusIcon} {status}
                          </p>
                          {status === "Shortlisted" || status === "Pending" ? (
                            <Button
                              variant="danger"
                              onClick={() => setWithdrawModal(true)}
                            >
                              Withdraw
                            </Button>
                          ) : null}
                        </Col>
                      </Row>
                      <Modal
                        size="md"
                        show={withdrawModal}
                        onHide={() => setWithdrawModal(false)}
                        centered
                      >
                        <Modal.Header closeButton>
                          <Modal.Title>
                            <Heading
                              content="Request Application Withdrawal"
                              design="h5"
                            />
                          </Modal.Title>
                        </Modal.Header>
                        <Form onSubmit={requestWithdrawFormSubmissionHandler}>
                          <Modal.Body>
                            <Col sm={12} className="form-group mb-3">
                              <Form.Control
                                type="hidden"
                                name="grabID"
                                value={grabID}
                              />

                              <label
                                htmlFor="withdrawReason"
                                className="required"
                              >
                                Reason for withdrawal
                              </label>
                              <Form.Check
                                type="radio"
                                id="default-radio"
                                label="I secured an offer from this website"
                                value="I secured an offer from this website"
                                name="reqWithdraw"
                                onClick={(e) =>
                                  setRequestWithdraw(e.target.value)
                                }
                              />
                              <Form.Check
                                type="radio"
                                id="default-radio"
                                label="I secured an offer from other job portal"
                                value="I secured an offer from other job portal"
                                name="reqWithdraw"
                                onClick={(e) =>
                                  setRequestWithdraw(e.target.value)
                                }
                              />
                              <Form.Check
                                type="radio"
                                id="default-radio"
                                label="I secured an offer offline"
                                value="I secured an offer offline"
                                name="reqWithdraw"
                                onClick={(e) =>
                                  setRequestWithdraw(e.target.value)
                                }
                              />
                              <Form.Check
                                type="radio"
                                id="default-radio"
                                label="I accidentally applied for the job"
                                value="I accidentally applied for the job"
                                name="reqWithdraw"
                                onClick={(e) =>
                                  setRequestWithdraw(e.target.value)
                                }
                              />

                              <Form.Check
                                type="radio"
                                id="default-radio"
                                label="Other"
                                value="Other"
                                name="reqWithdraw"
                                onClick={(e) =>
                                  setRequestWithdraw(e.target.value)
                                }
                              />
                            </Col>
                          </Modal.Body>
                          <Modal.Footer>
                            <Button
                              variant="danger"
                              type="submit"
                              disabled={!requestWithdrawFormIsValid}
                            >
                              Withdraw
                            </Button>
                          </Modal.Footer>
                        </Form>
                      </Modal>
                    </>
                  );
                })}
              </div>
            </div>
          </Col>
          <Col lg={2} md={2} sm={12} className="px-1 py-3 my-1 mx-auto">
            <Ads />
          </Col>
        </Row>
      </div>
    </AuthContext.Provider>
  );
}

export default User;
