function Home(props) {
  const [postList, setPostList] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/home/postList`).then(
      (response) => {
        setPostList(response.data);
      }
    );
  }, []);








  const userID = props.userID;
  const postOwnerID = props.postOwnerID;
  const sharePostLink = props.sharePostLink;

  if (props.postCompany === 1) {
    var postCompanyLink =
      "/partner/" +
      props.postCompanyName.toLowerCase().trim().split(/\s+/).join("-");
  }

  const [viewProfile, setViewProfile] = useState("avatar/male.svg");

