                    {postCompany === 1 ? (
                      <a
                        href={postCompanyLink}
                        target="_blank"
                        rel="noreferrer"
                        className="text-decoration-none text-dark"
                      >
                        {" "}
                        {postCompanyName}
                      </a>
                    ) : (
                      <a
                        href="/home"
                        className="text-decoration-none text-dark"
