                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  onClick={test}
                >
                  <BsPencilSquare className="icon" />
                  Edit
                </button>
              </li>
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  onClick={test}
                >
                  <BsTrash className="icon" /> Delete
                </button>
              </li>
              <li>
                <hr className="dropdown-divider" />
              </li>
              </>}
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  onClick={test}
                >
                  <BsFlag className="icon" />
                  Report
                </button>
              </li>
            </ul>
          </div>
        </Col>
      </Row>
      <Row className="mb-3">
        <Col sm={12} className="px-3">

          {postText !== "" && (
            <div
            dangerouslySetInnerHTML={{
              __html: postText.replace(/href/g, "target='_blank' href"),
            }}
          />
          )}
          {postImageName !== "" && (
            <img src={postImage} alt="rvr" className="w-100 mb-2" />
          )}
          {postVideoName !== "" && (
            <video controls className="d-flex w-75 mx-auto">
              <source
                className="w-75"
                alt="Video Uplnoad"
                src={postVideo}
              />
            </video>
          )}
        </Col>
      </Row>
      <Row>
        <Col sm={12}>
          <hr className="dropdown-divider" />
        </Col>
        <Col xs={4} sm={4} className="text-center link-primary">
          <FaThumbsUp className="icon icon-xs" />
          Like
          {/* <FaRegThumbsUp className="icon" /> */}
        </Col>
        <Col
          xs={4}
          sm={4}
          className="text-center border-start border-end px-0 px-md-1 px-lg-1"
          onClick={() => setOpen(!open)}
          aria-expanded={open}
          aria-controls="collapseID"
          role="button"
        >
          <a href="/s" className="link-dark text-decoration-none">
            <FaComments className="icon icon-xs" />
            Comment
          </a>
        </Col>
        <Col
          xs={4}
          sm={4}
          className="text-center"
          role="button"
          onClick={() => copyPostLink(sharePostLink)}
        >
          <FaShare className="icon icon-xs" />
          Share
        </Col>
      </Row>
      <Collapse in={open} className="pt-1 px-1" id="collapseID">
        <Container>
          <Row className="align-items-center py-3">
            <Col xs={1} sm={1} className="p-0 ps-2 ps-xs-1">
              <img
                src={UserImg}
                alt="eger"
                className=" col w-100 rounded-circle"
              />
            </Col>
            <Col xs={8} sm={9} className="pe-xs-0">
              <div className=" d-flex">
                <input
                  type="text"
                  className="form-control form-control-sm"
                  id="comment"
                  placeholder="Comments"
                />
              </div>
            </Col>
            <Col xs={2} sm={2} className="p-0">
              <button className="btn btn-sm btn-outline-primary">
                Comment
              </button>
            </Col>
          </Row>
          <Row className=" py-3">
            <Col xs={1} sm={1} className="p-0 ps-2">
              <img
                src={UserImg}
                alt="eger"
                className=" col w-100 rounded-circle"
              />
            </Col>
            <Col xs={11} sm={11}>
              <div className="p-2 border rounded bg-light bg-gradient">
                <p>
