import React, { Fragment, useEffect, useState } from "react";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { useHistory } from "react-router-dom";
import { AuthContext } from "../Helpers/AuthContext";
import Axios from "axios";

import { Container, Col, Row, InputGroup, Form } from "react-bootstrap";

//Components
import Heading from "../Components/Heading";

function BasicInfo() {
  let history = useHistory();

  const [authState, setAuthState] = useState({
    id: "",
    admin: "",
    company: "",
    status: false,
    attempt: "",
  });

  useEffect(() => {
    Axios.get("http://localhost:3001/auth/authenticate", {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      if (response.data.error) {
        setAuthState({ ...authState, status: false });
        history.push("/");
      } else {
        setAuthState({
          id: response.data.id,
          admin: response.data.admin,
          company: response.data.company,
          attempt: response.data.attempt,
          status: true,
        });
      }
    });
  }, []);

  const userBasicInfoFormSubmissionHandler =() => {};

  return (
    <Fragment>
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <HelmetProvider>
          <Helmet>
            <title> Basic Info | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
        <div id="basicInfo" className="basicInfo">
          <Container className="my-5">
            <Row>
              <Col sm={6} xs={12} className="mx-auto">
                <h1>{authState.id}</h1>
                <Heading content="Basic Info" />
                <Form onSubmit={userBasicInfoFormSubmissionHandler}>

                </Form>
              </Col>
            </Row>
          </Container>
        </div>
      </AuthContext.Provider>
    </Fragment>
  );
}

export default BasicInfo;
