import React, { Fragment } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';

//Toast
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Verify() {

    // const [token, setToken] = useState("");
    // let  { lff }  = useParams();
    // setToken(lff);

    const { token } = useParams();

    if (token){
        Axios.post('http://localhost:3001/verify/token', {
            token: token
        })
            .then((response) => {
                if (response.data === 'http://localhost:3000/index') {
                    return toast.success(
                        <Fragment>
                            <BiMessageCheck />
                            <span>fcbfbfdbfbdbdfb</span>
                        </Fragment>);
                } else {
                    <Fragment>
                        <BiError />
                        <span>System is Error. Please Try Again!</span>
                    </Fragment>
                }
            });
    } else {
        window.location.href = '/index';
    }
    
    // const ddd = window.location.href = '/index';

    return (
     <div>
      <h2>Now showing post {id}</h2>
     </div>
    );


}

export default Verify;
