import React, { Fragment, useState, useEffect } from "react";
import  { Redirect } from 'react-router-dom'
import { Helmet, HelmetProvider } from "react-helmet-async";
import Axios from "axios";
import useInput from "../Hooks/use-input";

// import DatePicker from "react-datepicker";
import { Typeahead } from "react-bootstrap-typeahead";
import DatePicker from "react-datepicker";
import { Container, Col, Row, InputGroup, Form } from "react-bootstrap";

//Componnets
import Header from "../Components/Header";
import Heading from "../Components/Heading";
import { toast } from 'react-toastify';


const isNotEmpty = (value) => value.trim() !== "";

function BasicInfo(props) {


  const [employer, setEmployer] = useState("");
  const [userID, setuserID] = useState("");

  Axios.defaults.withCredentials = true;
  useEffect(() => {
    Axios.get("http://localhost:3001/auth/login").then((response) => {
        if (response.data.loggedIn === true) {
          console.log(response);
            setEmployer(response.data.user[0].email);
            setuserID(response.data.user[0].id);
            console.log(response.data);
            console.log("hi");
        } else {
          return <Redirect to='/partner'  />
        }
      });
}, []);

  // Gender Input Check
  const {
    value: enteredGender,
    isValid: enteredGenderIsValid,
    hasError: genderInputHasError,
    valueChangeHandler: genderChangeHandler,
    inputBlurHandler: genderBlurHandler,
    reset: resetGenderInput,
  } = useInput(isNotEmpty);

  //DOB Input Check
  const {
    value: enteredDob,
    isValid: enteredDobIsValid,
    hasError: dobInputHasError,
    valueChangeHandler: dobChangeHandler,
    inputBlurHandler: dobBlurHandler,
    reset: resetDobInput,
  } = useInput(isNotEmpty);

  //About Input Check
  const {
    value: enteredAbout,
    isValid: enteredAboutIsValid,
    hasError: aboutInputHasError,
    valueChangeHandler: aboutChangeHandler,
    inputBlurHandler: aboutBlurHandler,
    reset: resetAboutInput,
  } = useInput(isNotEmpty);

  //School Input Check
  // const {
  //   value: enteredSchool,
  //   isValid: enteredSchoolIsValid,
  //   hasError: schoolInputHasError,
  //   valueChangeHandler: schoolChangeHandler,
  //   inputBlurHandler: schoolBlurHandler,
  //   reset: resetSchoolInput,
  // } = useInput(isNotEmpty);

  //Highest Education Level Check
  const {
    value: enteredHighLevel,
    isValid: enteredHighLevelIsValid,
    hasError: highLevelInputHasError,
    valueChangeHandler: highLevelChangeHandler,
    inputBlurHandler: highLevelBlurHandler,
    reset: resetHighLevelInput,
  } = useInput(isNotEmpty);

  //Current Status Level Check
  const {
    value: enteredCurrentStatus,
    isValid: enteredCurrentStatusIsValid,
    hasError: currentStatusInputHasError,
    valueChangeHandler: currentStatusChangeHandler,
    inputBlurHandler: currentStatusBlurHandler,
    reset: resetCurrentStatusInput,
  } = useInput(isNotEmpty);

  let basicInfoFormIsValid = false;

  if (
    enteredGenderIsValid &&
    enteredDobIsValid &&
    enteredAboutIsValid &&
    enteredHighLevelIsValid &&
    enteredCurrentStatusIsValid
  ) {
    basicInfoFormIsValid = true;
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (
      !enteredGenderIsValid &&
      !enteredDobIsValid &&
      !enteredAboutIsValid &&
      !enteredHighLevelIsValid &&
      !enteredCurrentStatusIsValid
    ) {
      return;
    } else {
      Axios.post('http://localhost:3001/basicinfo/user', {
      userID: userID,  
      gender: enteredGender,
        dob: enteredDob,
        about: enteredAbout,
      })
      .then ((response) => {
          return (
                  console.log(response.data)
          );
      })
    }

    console.log("Can Submit to BE");
    console.log(enteredGender);
    console.log(enteredDob);
    console.log(enteredAbout);
    console.log(enteredHighLevel);
    console.log(enteredCurrentStatus);
    // console.log(enteredSchool);
    resetGenderInput();
    resetDobInput();
    resetAboutInput();
    // resetSchoolInput();
    resetHighLevelInput();
    resetCurrentStatusInput();
  };

  const dobClasses = dobInputHasError
    ? "form-control form-control-sm is-invalid"
    : "form-control form-control-sm";

  const aboutClasses = aboutInputHasError
    ? "form-control form-control-sm is-invalid"
    : "form-control form-control-sm";

  const highLevelClasses = highLevelInputHasError
    ? "form-select form-select-sm is-invalid"
    : "form-select form-select-sm";

  // const schoolClasses = schoolInputHasError
  //   ? "form-control is-invalid"
  //   : "form-control";

  // School Suggestion
  const [schoolSelections, setSchoolSelections] = useState([]);
  const [universityList, setUniversityList] = useState([]);
  const [eduList, setEduList] = useState([]);
  useEffect(() => {
    Axios.get("http://localhost:3001/meta/university").then((response) => {
      setUniversityList(response.data);
      console.log(response.data);
    });
  }, []);

  //Education Level
  useEffect(() => {
    Axios.get("http://localhost:3001/meta/eduLevel").then((response) => {
      setEduList(response.data);
      console.log(response.data);
    });
  }, []);

  const [currentStatus, setCurrentStatus] = useState(0); // 0: no show, 1: show yes, 2: show no.
  const jobStatus = (currentStatus) => {
    setCurrentStatus(currentStatus);
  };

  return (
    <Fragment>
      <Header />
      <HelmetProvider>
        <Helmet>
          <title> Basic Info | Zom - IN </title>
        </Helmet>
      </HelmetProvider>

      <Container className="my-5">
        <Row>
          <Col sm={6} xs={12} className="mx-auto">
        {employer === "samiksha.zomin@gmail.com" ? "Employer" : "USer" }
        <h3>{employer}</h3>
          <h2>{userID}</h2>
          <h1>{userID=== "" ? "empty" : "have"}</h1>



            <Heading content="Basic Info" />
            <Form onSubmit={formSubmissionHandler}>
              {/* Gender */}
              <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputgender" className="required">
                  You are
                </label>
                <InputGroup>
                  <div>
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="male"
                      autoComplete="off"
                      onChange={genderChangeHandler}
                      onBlur={genderBlurHandler}
                      value="Male"
                      value={enteredGender}
                    />
                    <label className="btn btn-outline-success" htmlFor="male">
                      Male
                    </label>
                  </div>

                  <div className="ms-2 ">
                    <input
                      type="radio"
                      className="btn-check"
                      name="gender"
                      id="female"
                      autoComplete="off"
                      onChange={genderChangeHandler}
                      onBlur={genderBlurHandler}
                      value={enteredGender}
                      value="Female"
                    />
                    <label className="btn btn-outline-info" htmlFor="female">
                      Female
                    </label>
                  </div>
                </InputGroup>
                {genderInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please choose your gender.
                  </Form.Control.Feedback>
                )}
              </Col>

              {/* DAte of Birth */}
              <Col sm={12} className="form-group mb-3">
                <label htmlFor="inputdob" className="required">
                  Date of Birth
                </label>
                <input
                  type="date"
                  id="inputdob"
                  placeholder="Your Birthday"
                  className={dobClasses}
                  onChange={dobChangeHandler}
                  onBlur={dobBlurHandler}
                  value={enteredDob}
                />
                {dobInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please select your birthdate.
                  </Form.Control.Feedback>
                )}
              </Col>

              {/* About Me */}
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="inputabout" className="required">
                  About
                </label>
                <Form.Control
                  as="textarea"
                  placeholder="Tell us about you"
                  className={aboutClasses}
                  onChange={aboutChangeHandler}
                  onBlur={aboutBlurHandler}
                  value={enteredAbout}
                />
                {aboutInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Tell us about you.
                  </Form.Control.Feedback>
                )}
              </Col>
              <hr className="my-4" />

              <Heading content="Education" />
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="inputSchool" className="required">
                  School
                </label>

                <Form.Group>
                  <Typeahead
                  allowNew={true}
                  caseSensitive
                  ignoreDiacritics={false}
                    className="is-valid labelkey-example"
                    id="valid-styles-example"
                    isValid
                    labelKey={(option) => `${option.university_name}`}
                  options={universityList}
                  />
                  <Form.Control.Feedback type="valid">
                    Feedback
                  </Form.Control.Feedback>
                </Form.Group>



                {/*  <Typeahead
                  allowNew={true}
                  id="basic-typeahead-single"
                  onChange={setSchoolSelections}
                  labelKey={(option) => `${option.university_name}`}
                  options={universityList}
                  placeholder="Choose a state..."
                  selected={schoolSelections}
                  newSelectionPrefix="Add a new item: "
                  // className={schoolClasses}
                  // onChange={schoolChangeHandler}
                  // onBlur={schoolBlurHandler}
                  //isInvalid
                  //className="is-invalid"
                  // value={enteredSchool}
                  inputProps={{
                    className: `${aboutClasses}`,
                    required: true,
                  }}
                />

                {aboutInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Tell us about you.
                  </Form.Control.Feedback>
                )}*/}
              </Col>

              {/* Highest Education Level  */}
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="eduLevel" className="required">
                  Highest Education Level
                </label>
                <select
                  defaultValue={"DEFAULT"}
                  className=""
                  aria-label="Default select example"
                  className={highLevelClasses}
                  onChange={highLevelChangeHandler}
                  onBlur={highLevelBlurHandler}
                >
                  <option>Please Choose One</option>
                  {eduList.map((eduList, key) => {
                    const enteredHighLevel = eduList.child_name;
                    return (
                      <option key={enteredHighLevel} value={enteredHighLevel}>
                        {" "}
                        {enteredHighLevel}{" "}
                      </option>
                    );
                  })}
                </select>

                {highLevelInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please choose your highest education Level.
                  </Form.Control.Feedback>
                )}
              </Col>

              <Col sm={12} className="form-group mb-2">
                <label htmlFor="eduYear" className="required">
                  Year
                </label>
                <InputGroup className="mb-3">
                  <Col sm={4} xs={6}>
                    <DatePicker
                      // required
                      showYearPicker
                      dateFormat="yyyy"
                      yearItemNumber={10}
                      placeholderText="Year Start"
                    />
                  </Col>
                  <Col>
                    <DatePicker
                      // selected={startDate}
                      // onChange={(date) => setStartDate(date)}
                      showYearPicker
                      dateFormat="yyyy"
                      yearItemNumber={10}
                      placeholderText="Year End"
                    />
                  </Col>
                </InputGroup>
              </Col>

              <hr className="my-4" />
              <Heading content="Working Experience" />

              <Col sm={12} className="form-group mb-2">
                <label htmlFor="currentStatus" className="required">
                  Current Status
                </label>

                <InputGroup className="mb-3">
                  <div>
                    <input
                      type="radio"
                      className="btn-check"
                      name="status"
                      id="employedCS"
                      autoComplete="off"
                      onChange={currentStatusChangeHandler}
                      onBlur={currentStatusBlurHandler}
                      value={enteredCurrentStatus}
                      value="employee"
                      checked={currentStatus === 2}
                      onClick={(e) => jobStatus(2)}
                    />
                    <label className="btn btn-outline-success" htmlFor="employedCS">
                      Employed
                    </label>
                  </div>

                  <div className="ms-2 ">
                    <input
                      type="radio"
                      className="btn-check"
                      name="status"
                      id="unemployedCS"
                      autoComplete="off"
                      onChange={currentStatusChangeHandler}
                      onBlur={currentStatusBlurHandler}
                      value={enteredCurrentStatus}
                      value="unemployed"
                      checked={currentStatus === 1}
                      onClick={(e) => jobStatus(1)}
                    />
                    <label className="btn btn-outline-info" htmlFor="unemployedCS">
                      Unemployed
                    </label>
                  </div>

                  <div className="ms-2 ">
                    <input
                      type="radio"
                      className="btn-check"
                      name="status"
                      id="internCS"
                      autoComplete="off"
                      onChange={currentStatusChangeHandler}
                      onBlur={currentStatusBlurHandler}
                      value={enteredCurrentStatus}
                      value="intern"
                      checked={currentStatus === 3}
                      onClick={(e) => jobStatus(3)}
                    />
                    <label className="btn btn-outline-warning" htmlFor="internCS">
                      Intern
                    </label>
                  </div>
                </InputGroup>
                {currentStatusInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Please choose your current status.
                  </Form.Control.Feedback>
                )}
              </Col>

              {currentStatus > 1 && (
                <div>
                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="companyName" className="required">
                      Company Name
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      id="companyName"
                      placeholder="Company Name"
                    />
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="currentPos" className="required">
                      Current Position
                    </label>
                    <input
                      size="sm"
                      type="text"
                      className="form-control"
                      id="currentPos"
                      placeholder="Current Position"
                    />
                  </Col>
                </div>
              )}

              <hr className="my-4" />
              <Heading content="Skills & Experience" />

              {/* Skills & Experience" */}
              <Col sm={12} className="form-group mb-2">
                <label htmlFor="inputskillexp">Skills & Experience</label>
                <Form.Control
                  as="textarea"
                  placeholder="Please use comma(,) to separate your skills"
                  // className={aboutClasses}
                  // onChange={aboutChangeHandler}
                  // onBlur={aboutBlurHandler}
                  // value={enteredAbout}
                />
                {/* {aboutInputHasError && (
                  <Form.Control.Feedback type="invalid">
                    Tell us about you.
                  </Form.Control.Feedback>
                )} */}

              </Col>

              <Col className="text-end my-3">
                <button
                  type="submit"
                  className=" btn btn-primary"
                  disabled={!basicInfoFormIsValid}
                >
                  Submit
                </button>
              </Col>
            </Form>
          </Col>
        </Row>
      </Container>
    </Fragment>
  );
}

export default BasicInfo;
