import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
// import { Helmet } from 'react-helmet';
import Axios from "axios";
import routes from "./Config/routes";
import NotFound from "./Pages/NotFound";
import Logo from "./Components/Logo";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "react-datepicker/dist/react-datepicker.css";
import "react-bootstrap-typeahead/css/Typeahead.css";

import "./App.css";

import { AuthContext } from "./Helpers/AuthContext";

import { Navbar, Container, Nav, Button } from "react-bootstrap";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";

import "../node_modules/bootstrap/dist/js/bootstrap.bundle.min.js";

function App() {
  const [authState, setAuthState] = useState({
    id: "",
    admin: "",
    company: "",
    attempt: "",
    status: false,
  });

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/auth/authenticate`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      if (response.data.error) {
        setAuthState({ ...authState, status: false });
      } else {
        setAuthState({
          id: response.data.id,
          admin: response.data.admin,
          company: response.data.company,
          attempt: response.data.attempt,
          status: true,
        });
      }
    });
  });

  const logout = () => {
    localStorage.removeItem("accessToken");
    setAuthState({
      id: "",
      admin: "",
      company: "",
      attempt: "",
      status: false,
    });
  };

  return (
    <div>
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
          <Navbar
            collapseOnSelect
            // bg="white"
            variant="light"
            expand="lg"
            sticky="top"
            className="border-bottom navbar-light" 
          >
            <Container>
              <Navbar.Brand href="/">
                <Logo />
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">
                <Nav className="me-auto">
                {authState.status && 
                  <>
                  <Nav.Link href="/">Home</Nav.Link>
                  <Nav.Link href="/network">Networks</Nav.Link>
                  </>
                }
                  <Nav.Link href="/partner">Partners</Nav.Link>
                  <Nav.Link href="/opportunity">Opportunity</Nav.Link>
                  <Nav.Link href="whatshappening">What's Happening</Nav.Link>
                  <Nav.Link href="#features">Spice-IN</Nav.Link>
                {!authState.status && 
                  <Nav.Link href="#pricing">About Us</Nav.Link>
                }
                </Nav>
                {!authState.status ? (
                  <Nav>
                    <Button
                      variant="outline-primary"
                      className="m-1"
                      href="/SignUp"
                    >
                      Sign Up
                    </Button>
                    <Button
                      variant="outline-success"
                      className="m-1"
                      href="/employer"
                    >
                      For Employer
                    </Button>
                  </Nav>
                ) : (
                  <Nav>
                    <div className="dropdown text-end">
                      <a href="#fdf"
                        className="d-block link-dark text-decoration-none dropdown-toggle"
                        id="dropdownUser1"
                        data-bs-toggle="dropdown"
                        data-bs-display="static"
                        aria-expanded="false">
                        <img
                          src="https://github.com/mdo.png"
                          alt="mdo"
                          width="32"
                          height="32"
                          className="rounded-circle"
                        />
                      </a>
                      <ul
                        className="dropdown-menu dropdown-menu-lg-end"
                        aria-labelledby="dropdownUser1"
                      >
                        <li>
                          <a className="dropdown-item" href="/dashboard">
                            Dashboard
                          </a>
                        </li>
                        <li>
                          <a className="dropdown-item" href="/profile">
                            Profile
                          </a>
                        </li>
                        <li>
                          <a className="dropdown-item" href="/chat">
                            Chat
                          </a>
                        </li>
                        <li>
                          <a className="dropdown-item" href="/setting">
                            Settings
                          </a>
                        </li>
                        <li>
                          <a className="dropdown-item" href="About Us">
                            About Us
                          </a>
                        </li>
                        <li>
                          <hr className="dropdown-divider" />
                        </li>
                        <li onClick={logout}>
                          <button className="dropdown-item">
                            Sign Out
                          </button>
                        </li>
                      </ul>
                    </div>
                  </Nav>
                )}
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </Router>

        <Router>
          <Switch>
            {routes.map(({ path, component, name }) => {
              return (
                <Route exact path={path} key={name} component={component} />
              );
            })}

            <Route component={NotFound} />
          </Switch>

          <ToastContainer
            position="bottom-left"
            autoClose={5000}
            hideProgressBar={false}
            newestOnTop={false}
            closeOnClick
            rtl={false}
            pauseOnFocusLoss
            draggable
            pauseOnHover
          />
        </Router>
      </AuthContext.Provider>
    </div>
  );
}

export default App;
