import React from "react";
import { Col, Form } from "react-bootstrap";
import { BiChevronLeft } from "react-icons/bi";
import { BsEyeFill } from "react-icons/bs";

import Heading from "../Heading";

function Password({ setshowSubContentClick }) {

    const revealPassword = (obj) => {
        // var obj = document.getElementById('myPassword'+obj);
        // obj.type = "text";
        console.log(obj);
      }

      const hidePassword = (obj) => {
        var obj = document.getElementById('myPassword'+obj);
        obj.type = "password";
      }
  return (
    <div>
      <h1
        onClick={() => setshowSubContentClick(0)}
        className="me-2 d-block d-sm-none"
      >
        <BiChevronLeft className="d-inline" />
        <Heading content="Password" design="h1 d-inline-block" />
      </h1>
      <Col lg={10} md={12} sm={12} className="mx-auto my-2">
        <Heading content="Change Password" design=" h3 mb-4" />

        <Form>
          <Col sm={12} className="form-group mb-3">
            <label htmlFor="studyField" className="required">
              Current Password
            </label>
            <div className="input-group mb-3">
              <input
                type="password"
                className="form-control form-control-sm"
                id="myPassword3"
                //   className={nameClasses}
                //   onChange={nameChangeHandler}
                //   onBlur={nameBlurHandler}
                //   value={enteredName}
              />
              <span className="input-group-text" id="basic-addon2" onMouseOver="revealPassword(3)" onMouseOut="hidePassword(3)">
                <BsEyeFill />
              </span>
            </div>

            {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
          </Col>

          <Col sm={12} className="form-group mb-3">
            <label htmlFor="studyField" className="required">
              New Password
            </label>
            <div className="input-group mb-3">
              <input
                type="password"
                className="form-control form-control-sm"
                id="myPassword2"
                //   className={nameClasses}
                //   onChange={nameChangeHandler}
                //   onBlur={nameBlurHandler}
                //   value={enteredName}
              />
              <span className="input-group-text" id="basic-addon2" onMouseOver="revealPassword(2)" onMouseOut="hidePassword(2)">
                <BsEyeFill />
              </span>
            </div>

            {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
          </Col>

          <Col sm={12} className="form-group mb-3">
            <label htmlFor="studyField" className="required">
              Confirm Password
            </label>
            <div className="input-group mb-3">
              <input
                type="password"
                className="form-control form-control-sm"
                id="myPassword1"
                //   className={nameClasses}
                //   onChange={nameChangeHandler}
                //   onBlur={nameBlurHandler}
                //   value={enteredName}
              />
              <span className="input-group-text" id="basic-addon2" onMouseOver="revealPassword(1)" onMouseOut="hidePassword(1)">
                <BsEyeFill />
              </span>
            </div>

            {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
          </Col>

          <Col className="text-end">
            <button type="reset" class="btn btn-outline-secondary">
              Reset
            </button>{" "}
            <button type="submit" class="btn btn-primary">
              Save
            </button>
          </Col>
        </Form>
      </Col>
    </div>
  );
}

export default Password;
