import React, { useState } from "react";
import { Modal } from "react-bootstrap";

function ApplicationCard({ onClick, cddStatus, grabID }) {
    const [showModal, setShowModal] = useState(false);
    const [statusValue, setStatusValue] = useState(cddStatus);

    const changingStatus= (e) => {
        setShowModal(true);
        setStatusValue(e.target.value);
        console.log(statusValue)
    }

  return (
    <div>
      <select
        className="form-select form-select-sm text-center"
        onChange={changingStatus}
        value={statusValue}
      >
        <option value="0">Pending</option>
        <option value="3">Shortlisted</option>
        <option value="2">Hired</option>
        <option value="1">Rejected</option>
      </select>

      <Modal show={showModal} 
      onHide={() => setShowModal(false)}
      >
      {statusValue === 2 ? <h1>hiring</h1>
      :
      <h1>not hiring </h1>
      }

      <button onClick={() => onClick(statusValue,grabID)}>click me</button>
      </Modal>
    </div>
  );
}

export default ApplicationCard;
