import React, { Fragment, useState } from 'react';
import { Helmet, HelmetProvider } from "react-helmet-async";
import { Container, Row, Col } from "react-bootstrap";

import Heading from '../../Heading';
import SubMenu from './SubMenu';
import User from './User';


function Menu() {

    const [showSubContent, setshowSubContent] = useState(1);
    const subContentStatus = (currentSubContent) => {
      setshowSubContent(currentSubContent);
    };


    let mobileStatus;
    if (window.innerWidth < 426) {
      if (showSubContent > 0) {
          mobileStatus = "d-none";
      } else {
          mobileStatus = "d-block";
      }
    }


    return (
        <Fragment>
        <HelmetProvider>
          <Helmet>
            <title> User | Zom - IN </title>
          </Helmet>
        </HelmetProvider>
        <div id="admin" className="admin my-2">
          <Container>
            <Row>
            <Heading content="User" design="h5" />

              <Col lg={2} md={3} sm={12} className={`mb-4 ${mobileStatus}`}>
                <Row
                  className="d-flex justify-content-between border-bottom"
                  onClick={(e) => subContentStatus(1)}
                  role="button"
                >
                  <SubMenu content="User" />
                </Row>

                <Row
                  className="d-flex justify-content-between border-bottom"
                  onClick={(e) => subContentStatus(2)}
                  role="button"
                >
                  <SubMenu content="Verified" />
                </Row>

              </Col>

              <Col lg={8} md={9} sm={12} className={!mobileStatus}>

                {showSubContent === 1 && (
                  <User showSubContent={showSubContent} setshowSubContentClick={setshowSubContent} />
                )}

              </Col>
              <Col lg={2} className="d-none d-md-none d-lg-block border">
                <Heading content="Ads" design="h5" />
              </Col>
            </Row>
          </Container>
        </div>
      </Fragment>
    )
}

export default Menu
