import React, { Fragment, useEffect, useState, useContext } from "react";
import { AuthContext } from "../../Helpers/AuthContext";
import Axios from "axios";

import { Row, Col, Button, Form } from "react-bootstrap";
import {
  BiTime
} from "react-icons/bi";

import Ads from "../Ads";
import Profile from "../Profile";
import Name from "../Name";
import Heading from "../Heading";

function User() {
  const { authState, setAuthState } = useContext(AuthContext);
  const [interviewList, setInterviewList] = useState([]);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/userInterview/list`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      setInterviewList(response.data);
      console.log(response.data);
    });
  }, []);

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <div>
        <Row className="mx-auto p-5">
          <Col lg={10} md={10} sm={12}>
          {interviewList
          .map((interviewDetails, key) => {
            const employerID = interviewDetails.bp_id;
            const interviewerName = interviewDetails.interviewerName;
            const interviewLink = interviewDetails.interviewVenueOrVideo;
          const interviewingTime = new Date(interviewDetails.interviewTime);
          const interviewTime = interviewingTime
            .toLocaleDateString("en-GB", {
              day: "numeric",
              month: "short",
              year: "numeric",
              hour: "2-digit",
              minute: "2-digit",
              hour12: true,
            })
            .replace(/ /g, " ");




            return (
              <Row className="mx-auto my-3 p-3 border-bottom">
              <Col sm={2} className="d-none d-md-none d-lg-block">
                <Profile
                  id={employerID}
                  design="rounded-circle"
                  width="100"
                  height="100"
                />
              </Col>
              <Col xs={12} sm={8}>
                <p className="m-0">Job title</p>
                <p className="m-0">
                  <Name id={employerID}/>
                </p>
                <p className="m-0">Intervierew Name: {interviewerName}</p>
                <p className="m-0"><BiTime className="icon"/>{interviewTime}</p>
                <a href={interviewLink} target="_blank" className="m-0 text-decoration-none">{interviewLink}</a>


                {/* <p>
                              <span
                                // className={`badge rounded-pill text-dark ${jobTypeBG}`}
                              >
                                <img
                                  alt="briefcaseIcon"
                                  className="icon"
                                  src={briefcaseIcon}
                                />
                                job type
                              </span>
                            </p> */}
              </Col>
              <Col sm={2} className="text-center m-auto">
                <Button>Re-Schedule</Button>
              </Col>
            </Row>
            );
          })
          }

          </Col>
          <Col lg={2} md={2} sm={12} className="px-1 py-3 my-1 mx-auto">
            <Ads />
          </Col>
        </Row>
      </div>
    </AuthContext.Provider>
  );
}

export default User;
