import React, { useState, Fragment } from "react";
import { Container, Row, Col, Form } from "react-bootstrap";
import UserImg from "../../Assests/Images/user/sha.jpeg";

import { FcCamera, FcVideoCall } from "react-icons/fc";
import { BsXCircleFill } from "react-icons/bs";

import { toast } from "react-toastify";

function CreatePost() {
  //Text Input
  const [postText, setpostText] = useState("");

  //Image Input
  const [postImage, setPostImage] = useState();
  const [postVideo, setPostVideo] = useState();
  const [imageLength, setImageLength] = useState(false);
  const [videoLength, setVideoLength] = useState(false);

  const imageChange = (event) => {
    let imageFile = event.target.files[0];

    if (event.target.files && event.target.files.length > 0) {
      setPostImage(imageFile);
      setImageLength(true);
    }

    if (event.target.files[0].size > 5e+6) {
      setPostImage();
      setImageLength(false);
      return toast.warn(
        <Fragment>
          <span>Please upload a file smaller than 5 MB</span>
        </Fragment>
      );
    }

    if (!imageFile.name.match(/\.(jpg|jpeg|png|gif)$/)) {
      setPostImage();
      setImageLength(false);
      return toast.warn(
        <Fragment>
          <span>Invalid File Upload. Please Try to Upload file with jpg, jpeg, png or gif</span>
        </Fragment>
      );
    }
  };


//   This post appears to be blank. Please write something or attach a link or photo to post. video - 50mb
  const removeSelectedImage = () => {
    setPostImage();
    setImageLength(false);
  };




  const videoChange = (event) => {
    let videoFile = event.target.files[0];

    if (event.target.files && event.target.files.length > 0) {
      setVideoImage(videoFile);
      setVideoLength(true);
    }

    if (event.target.files[0].size > 5e+7) {
      setVideoImage();
      setVideoLength(false);
      return toast.warn(
        <Fragment>
          <span>Please upload a file smaller than 50 MB</span>
        </Fragment>
      );
    }

    if (!videoFile.name.match(/\.(mp4|x-m4v)$/)) {
      setVideoImage();
      setVideoLength(false);
      return toast.warn(
        <Fragment>
          <span>Invalid File Upload. Please Try to Upload file with mp4</span>
        </Fragment>
      );
    }
  };
  const removeSelectedVideo = () => {
    setVideoImage();
    setVideoLength(false);
  };













  //Form Input
  let createPostFormIsValid = false;

    if (postText !== ""  || imageLength) {
    createPostFormIsValid = true;
  }


  const createPostFormSubmissionHandler = (event) => {
    event.preventDefault();
    alert("okay");
  };

  return (
    <div className="card py-3">
      <Container>
        <Row className="mx-auto my-2">
          <Col sm={1} className="d-flex">
            <img
              src={UserImg}
              className="img-fluid my-auto rounded-circle"
              alt="zom-in user"
            />
          </Col>
          <Col sm={11}>
            <div className="d-grid gap-2 createPost">
              <button
                className="btn btn-outline-secondary rounded-pill text-start p-3 fw-bold"
                type="button"
                data-bs-toggle="modal"
                data-bs-target="#staticBackdrop"
              >
                Share your thoughts...
              </button>
            </div>
          </Col>
        </Row>
        <Row>
          <Col sm={6} className="p-2 text-end">
            <button
              className="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              <FcCamera className="icon" /> Photos
            </button>
          </Col>
          <Col sm={6} className="p-2 text-start divider">
            <button
              className="btn btn-outline-light fw-bold border-0 text-dark themeColorUser"
              type="button"
              data-bs-toggle="modal"
              data-bs-target="#staticBackdrop"
            >
              <FcVideoCall className="icon" /> Videos
            </button>
          </Col>
        </Row>
        <div
          className="modal fade"
          id="staticBackdrop"
          data-bs-backdrop="static"
          data-bs-keyboard="false"
          tabIndex="-1"
          aria-labelledby="staticBackdropLabel"
          aria-hidden="true"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <Form onSubmit={createPostFormSubmissionHandler}>
                <div className="modal-body mt-3 mb-0 mx-0 p-0">
                  <div className="position-relative">
                    <div className="position-absolute top-0 end-0 p-2">
                      <button
                        type="button"
                        className="btn-close"
                        data-bs-dismiss="modal"
                        aria-label="Close"
                      ></button>
                    </div>
                  </div>

                  <textarea
                    className=" form-control w-100 border-0 p-4 fw-bold inputPost"
                    placeholder="Share your thoughts..."
                    onChange={(e) => setpostText(e.target.value)}
                    value={postText}
                  ></textarea>

                  {postImage && (
                    <div className="m-2 w-75 mx-auto border border-5 themeColorUser rounded imgPostPreview">
                      <div className="position-relative">
                        <div className="position-absolute top-0 start-100 translate-middle">
                          <button
                            onClick={removeSelectedImage}
                            className="bg-transparent rounded-circle border-0 position-relative"
                          >
                            <BsXCircleFill
                              size={21}
                              className="bg-white rounded-circle"
                            />
                          </button>
                        </div>
                      </div>
                      <img
                        src={URL.createObjectURL(postImage)}
                        className="w-100"
                        alt="Thumb"
                      />
                    </div>
                  )}


                  {postVideo && (
                    <div className="m-2 w-75 mx-auto border border-5 themeColorUser rounded imgPostPreview">
                      <div className="position-relative">
                        <div className="position-absolute top-0 start-100 translate-middle">
                          <button
                            onClick={removeSelectedVideo}
                            className="bg-transparent rounded-circle border-0 position-relative"
                          >
                            <BsXCircleFill
                              size={21}
                              className="bg-white rounded-circle"
                            />
                          </button>
                        </div>
                      </div>
                      <img
                        src={URL.createObjectURL(postVideo)}
                        className="w-100"
                        alt="Thumb"
                      />
                    </div>
                  )}

                  <Row className="p-3">
                    <Col sm={6} className="p-2 text-end">
                      <span className=" badge rounded-pill fw-bold border-0 text-dark p-3 fs-6 position-relative overflow-hidden themeColorUser inputPostFile">
                        <FcCamera className="icon" /> Photos
                        <input type="file" accept="image/*" onChange={imageChange} />
                      </span>
                    </Col>
                    <Col sm={6} className="p-2 text-start divider">
                      <span className="badge rounded-pill fw-bold border-0 text-dark p-3 fs-6 position-relative overflow-hidden themeColorUser inputPostFile">
                        <FcVideoCall className="icon" /> Videos
                        <input type="file" accept="video/mp4,video/x-m4v,video/*" onChange={videoChange} />
                      </span>
                    </Col>
                  </Row>

                  <div className="modal-footer">
                    <button
                      type="submit"
                      disabled={!createPostFormIsValid}
                      className="btn btn-primary"
                    >
                      Post
                    </button>
                  </div>
                </div>
              </Form>
            </div>
          </div>
        </div>
      </Container>
    </div>
  );
}

export default CreatePost;
