import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Row, Col } from "react-bootstrap";
import JobStatus from "./JobStatus";

import { BiCheckCircle, BiXCircle, BiMinusCircle } from "react-icons/bi";
import briefcaseIcon from "../../Assests/Images/icon/briefcase.svg";

function ApplicationCard(props) {
  const [viewName, setViewName] = useState("Zom-IN User");
  const [viewEmail, setViewEmail] = useState("");

  const grabID = props.grabID;
  const jobTitle = props.jobTitle;
  const jobType = props.jobType;
  const candidateID = props.candidateID;
  const cddStatus = props.cddStatus;


  const [jobStatus, setJobStatus] = useState({
    jobStatusModal: false,
    grabID: props.grabID,
    cddID: '',
    jobStatusNum: ''
 });




  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/dashboard/userName/` + candidateID
    ).then((response) => {
      const userDetails = response.data[0];
      setViewName(userDetails.name);
      setViewEmail(userDetails.email);
    });
  }, [candidateID]);

  let jobTypeBG;
  switch (jobType) {
    case "Contract":
      jobTypeBG = "bg-contract";
      break;
    case "Internship":
      jobTypeBG = "bg-internship";
      break;
    case "Part Time":
      jobTypeBG = "bg-partTime";
      break;
    default:
      jobTypeBG = "bg-fullTime";
  }

  const changeStatus = (grabID, status) => {
      console.log(grabID);
      console.log(status);
  }

  return (
    <>
      <Row>
        <Col lg={9} md={8} sm={12} className="fs-6 mb-2">
          <p className="mb-0 text-truncate fw-bold">{jobTitle}</p>
          <p className="mb-0 ms-2 text-truncate">
            <a href="/mm" target="_blank" className="text-decoration-none">
              {viewName}
            </a>
          </p>
          <p className="mb-0 ms-2 text-truncate">{viewEmail} </p>
          <p className="mb-0 ms-2 text-truncate">
            <span className={`badge rounded-pill text-dark ${jobTypeBG}`}>
              <img alt="briefcaseIcon" className="icon" src={briefcaseIcon} />
              {jobType}
            </span>
          </p>
          {cddStatus === 4 && 
          <div className="border border-0 rounded alert-warning my-1 ms-2 p-2">
            <p className="p-0 m-0">
              The candidate already secured a job in this website.
            </p>
          </div>
          }
        </Col>
        {cddStatus === 4 ?
            <Col lg={3} md={4} sm={12} className="text-center mx-auto">
            <h5><BiMinusCircle size={28} className="mx-2 text-warning"/>
            Withdrew</h5>
          </Col>

        : (<>

        {cddStatus === 2 ? (
            <Col lg={3} md={4} sm={12} className="text-center mx-auto">
            <h5><BiCheckCircle size={28} className="mx-2 text-success"/>
            Hired</h5>
          </Col>
           )
           :
           <>
           {cddStatus === 1 ? (
            <Col lg={3} md={4} sm={12} className="text-center mx-auto">
            <h5><BiXCircle size={28} className="mx-2 text-danger"/>
            Rejected</h5>
          </Col>
           )
           :
           (
            <Col lg={3} md={4} sm={12}>
          <button type="button" className="col-12 btn btn-sm btn-primary mb-2">
            Schedule
          </button>
          <select
            className="form-select form-select-sm text-center"
            value={cddStatus}
            onChange={(e) =>setJobStatus({...jobStatus, jobStatusModal: true,
    grabID: grabID,
    cddID: candidateID,
    jobStatusNum: e.target.value
    })}
          >
            <option defaultValue="0" value="0">Pending</option>
            <option value="3">Shortlisted</option>
            <option value="2" >Hired</option>
            <option value="1">Rejected</option>
          </select>
          <JobStatus 
          grabID={grabID}
        show={jobStatus}
                //   onHide={() => setJobStatus(false)}
                  />
        </Col>

           )}
           </>
           }
</>
        )
        }
      </Row>
      <hr className="mx-1 my-3" />
    </>
  );
}

export default ApplicationCard;
