    <div className="card mb-3">
      <img src={CoverImg} className="card-img-top profilecover" alt="Background Cover" />
      <div className="position-relative">
        <div className="position-absolute top-50 start-50 translate-middle w-50">
          <img src={UserImg} className="card-img-top rounded-circle" alt="Background Cover" />
        </div>
      </div>
      <div className="card-body mt-4 text-center pb-0">
      <h6 href="/edcwe" className="card-title pt-3"><a className="text-decoration-none link-dark" href="/profile">Samiksha Moghan</a></h6>
        <p className="card-text text-muted text-small">
          Web Developer at Zom-IN
        </p>
      </div>
      <hr className="dropdown-divider"/>
      <div className="mx-auto m-1">
          <a href="/profile" className="text-decoration-none link-primary">View Profile</a>
      </div>
    </div>
    );
      })}
    </AuthContext.Provider>
  );
}

export default Profile;
