import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle, BiXCircle } from "react-icons/bi";

import { Col, Row, Modal, Button } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function Language() {
  const navigate = useNavigate();

  const [showDelete, setShowDelete] = useState(false);
  const [languages, setLanguages] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/language/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
        setLanguages(response.data);
          }

      }
    );
  }
  }, []);


    return (
      <>
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="Language" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto"><BiPencil className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col>
          <div className="d-flex flex-wrap">
          {languages.map((languageDetails, key) => {
            const languageID = languageDetails.id;
            const language = languageDetails.language;

            return (
              <p key={languageID} className="badge rounded-pill alert-primary fw-normal text-dark m-2 fs-7">{language} <BiXCircle role="button" onClick={() => setShowDelete(true)} /></p>

            );
          })}
          </div>
          </Col>
        </Col>
      </Row>


      <Modal
                  size="md"
                  scrollable
                  show={showDelete}
                  onHide={() => setShowDelete(false)}
                >
                  <Modal.Header closeButton>
                    <Modal.Title>
                      <Heading content="Delete Education" design="h4 my-auto" />
                    </Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <p>Are you sure want to delete it?</p>
                  </Modal.Body>
                  <Modal.Footer>
                    <Button
                      variant="secondary"
                      onClick={() => setShowDelete(false)}
                    >
                      Close
                    </Button>
                    <Button type="submit" variant="primary" onClick={() => deleteLanguage(languageID)} >
                      Save Changes
                    </Button>
                  </Modal.Footer>
                </Modal>
      </>
    )
}

export default Language
