import React, { Fragment } from "react";
import Axios from 'axios';
import { useHistory } from "react-router-dom";

import { Modal, Button } from "react-bootstrap";
import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

//Toast
import { toast } from "react-toastify";

import Heading from "../Heading";

function ApplyJob(props) {
  let history = useHistory();


    const oppoID = props.oppo_id;
    const jobTitle = props.job_title;

    const applyJob = (id) => {
        let applyJobInfo = [id];
        console.log(applyJobInfo[0]);


        if (!localStorage.getItem("accessToken")) {
            return [
                history.push("/signup"),
                toast.warn(
                  <Fragment>
                   <BiMinusCircle /> <span>Please Sign In /Sign Up before applying for a job</span>
                  </Fragment>
                )
              ];


          } else {
        Axios.post(
            `${process.env.REACT_APP_SERVER_DOMAIN}/opportuntity/user/`,
            {
              gender: editPostData[1],

            },
            {
              headers: {
                accessToken: localStorage.getItem("accessToken"),
              },
            }
          ).then((response) => {
            if (response.data.error) {
              return toast.error(
                <Fragment>
                  <BiMinusCircle /> <span>{response.data.error}</span>
                </Fragment>
              );
            } else {
              return toast.success(
                <Fragment>
                  <BiMessageCheck /> <span>{response.data.success}</span>
                </Fragment>
              );
            }
          });
        }
      };


  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Heading content={`Applying for ${jobTitle}`} design="h5" />
      </Modal.Header>
      <Modal.Body>
        <p>Is your resume complete?</p>
        <p>
          If Yes, please click "Apply" and if No, then click "Go to Profile"
        </p>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" href="/profile">
          Go to Profile
        </Button>
        <Button onClick={() => applyJob(oppoID)}>Apply</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ApplyJob;
