import React from 'react';
import { useParams, Route } from 'react-router-dom';

function Verify() {

    // const [token, setToken] = useState("");
    // let  { lff }  = useParams();
    // setToken(lff);

    const { token } = useParams();

    if (token){
        Axios.post('http://localhost:3001/verify/token', {
            token: token
        })
            .then((response) => {
                if (response.data === 'Email sent successfully') {
                    return toast.success(
                        <Fragment>
                            <BiMessageCheck />
                            <span>Email sent successfully!</span>
                        </Fragment>);

                } else if (response.data === 'Email Exist!') {
                    return toast.error(
                        <Fragment>
                            <BiError />
                            <span>This Email already exists!</span>
                        </Fragment>
                    );
                } else if (response.data === 'Invalid Email!') {
                    return toast.error(
                        <Fragment>
                            <BiError />
                            <span>This Email is Invalid!</span>
                        </Fragment>
                    );
                } else {
                    <Fragment>
                        <BiError />
                        <span>System is Error. Please Try Again!</span>
                    </Fragment>
                }
            });
    } else {
        window.location.href = '/index';
    }
    
    // const ddd = window.location.href = '/index';

    return (
     <div>
      <h2>Now showing post {id}</h2>
     </div>
    );


}

export default Verify;
