function Test() {

    const [form, setForm] = useState([]);
    const [genderInput, setGenderInput] = useState([]);
    const [openM, setOpenM] = useState(false);
    const [openMs, setOpenMs] = useState("");


      useEffect(() => {
        Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/meta/test`)
            .then((response) => {
                // setForm([response.data]);
                console.log("here");
                setForm(response.data);

            });
    }, []);

     const updateField = e => {
         setForm ([
        {
            username: genderInput,
          password: '',
          favoriteFood: ''
        }, ...form])

        console.log(form);
        e.preventDefault();
    };

    function closeMs(name) {
        alert(`hello, ${name}`);
      }

    return (
        <div>
        <TestChild  closeMs={setOpenMs} />
        {openM ? "true" : "false"} 
        <button onClick={() => {
            setOpenM(true);
        }} > button </button>
            <form onSubmit={updateField}>
    <input type="text" placeholder="Name" name="username"
        onChange={(e) => setGenderInput(e.target.value)}
                        value={genderInput}
    />
    <input type="text" placeholder="Email" />
    <input type="text" placeholder="Email" />
    <button type="submit">Confirm</button>
</form>
<div>
    {form.map((formde, key) => {
        const u = formde.username;
        const us = formde.password;
        return (
            <div key={key}>
            <h1>{u}</h1>
            <h2>{us}</h2>
            </div>
        );
    })}
    </div>
        </div>
    )
}

export default Test;
