import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
// import { Helmet } from 'react-helmet';
import routes from "./Config/routes";
import NotFound from "./Pages/NotFound";

import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "react-datepicker/dist/react-datepicker.css";
import "react-bootstrap-typeahead/css/Typeahead.css";

import "./App.css";



import { useState } from "react";
import Logo from './Components/Logo';
import './Styles/header.css';

import { AuthContext } from './Helpers/AuthContext';


import { Navbar, Container, Nav, Button } from 'react-bootstrap';

function App() {

  const [authState, setAuthState] = useState(false);


  return (
    <div>
        <AuthContext.Provider value={{ authState, setAuthState }}>
      <Router>
        <Switch>
          {routes.map(({ path, component, name }) => {
            return <Route exact path={path} key={name} component={component} />;
          })}

          <Route component={NotFound} />
        </Switch>

        <Router>
            <Navbar collapseOnSelect bg="white" variant="light" expand="lg" sticky="top" className="border-bottom">
                <Container>
                    <Navbar.Brand href="/">
                        <Logo />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link href="/partner">Partners</Nav.Link>
                            <Nav.Link href="/opportunity">Opportunity</Nav.Link>
                            <Nav.Link href="whatshappening">What's Happening</Nav.Link>
                            <Nav.Link href="#features">Spice-IN</Nav.Link>
                            <Nav.Link href="#pricing">About Us</Nav.Link>
                            <Nav.Link href="/basicinfo">Testing</Nav.Link>

                        </Nav>
      {!authState &&

                        <Nav>
                            <Button variant="outline-primary" className="m-1" href="/SignUp">Sign Up</Button>
                            <Button variant="outline-success" className="m-1" href="/employer" >For Employer</Button>
                        </Nav>
      }

                    </Navbar.Collapse>
                </Container>
            </Navbar>


        </Router>
        <ToastContainer
          position="bottom-left"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
        />
      </Router>
      </AuthContext.Provider>

    </div>
  );
}

export default App;
