import React, { Fragment } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';

//Toast
import { BiError, BiMessageCheck } from "react-icons/bi";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Verify() {

    // const [token, setToken] = useState("");
    // let  { lff }  = useParams();
    // setToken(lff);

    const { token } = useParams();

    if (token){
        Axios.post('http://localhost:3001/verify/token', {
            token: token
        })
            .then((response) => {
                if (response.data === 'System dvvdsvdsv') {
                    return toast.success(
                        <Fragment>
                            <BiMessageCheck />
                            <span>fcbfbfdbfbdbdfb</span>
                        </Fragment>);
                } else {
                    <Fragment>
                        <BiError />
                        <span>System is Error. Please Try Again!</span>
                    </Fragment>
                }
            });
    } else {
        window.location.href = '/index';
        console.log("empty param");
    }
    
    // const ddd = window.location.href = '/index';

    return (
<Fragment>
<div>
      <h2>Now showing post {token}</h2>
      
     </div>
     <ToastContainer
                position="bottom-left"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />
</Fragment>
    );


}

export default Verify;
