import React, { useState, useEffect } from "react";
import Axios from "axios";
import ReactPaginate from "react-paginate";
import { Row, Col, Table } from "react-bootstrap";

import Heading from "../../Heading";

function TotalUser() {
  const [userList, setUserList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [pageNumber, setPageNumber] = useState(0);

  const animePerPage = 8;
  const pagesVisited = pageNumber * animePerPage;

  const pageCount = Math.ceil(animelist.length / animePerPage);
  const changePage = ({ selected }) => {
    setPageNumber(selected);
  };

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/zomAdminUser/totalUser`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      setUserList(response.data);
    });
  }, []);

  return (
    <Row className="mx-5">
    <Col sm={12} className="mx-auto my-2">
        <Heading content="Total User" design="h4" />
    </Col>
      <Col sm={12} className="mx-auto my-2">
        <Table responsive striped bordered hover size="sm">
          <thead>
            <tr>
              <th>#</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Username</th>
            </tr>
          </thead>
          <tbody>


          {/* .slice(pagesVisited, pagesVisited + animePerPage) */}
            <tr>
              <td>1</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
            </tr>
          </tbody>
        </Table>
        <ReactPaginate
        previousLabel={"Previous"}
        nextLabel={"Next"}
        pageCount={pageCount}
        onPageChange={changePage}
        containerClassName={"paginateBtn"}
        previousLinkClassName={"previousBtn"}
        nextLinkClassName={"nextBtn"}
        disabledClassName={"paginateDisabled"}
        activeClassName={"paginateActive"}
      />
      </Col>
    </Row>
  );
}

export default TotalUser;
