import React, { useState, useEffect, Fragment } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle } from "react-icons/bi";
import LocationIcon from "../../../../Assests/Images/icon/location.svg";


import { Col, Row, Modal, Button, Form } from "react-bootstrap";
import Heading from "../../../Heading";
import { toast } from "react-toastify";

function About() {
  const navigate = useNavigate();

  const [about, setAbout] = useState([]);
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/about/`,
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          );
        } else {
          setAbout(response.data);
        }
      });
    }
  }, []);

  return (
    <>
      <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
          <Row>
            <Col xs={10} sm={10} className="text-start">
              <Heading content="About Me" design="h5" />
            </Col>
            <Col xs={2} sm={2} className="text-end align-self-center">
              <p
                role="button"
                className="my-auto"
                onClick={() => setShow(true)}
              >
                <BiPencil className="icon" />
              </p>
            </Col>
          </Row>
        </Col>
        <Col sm={12}>
          <Col>
            {about.map((aboutDetails, key) => {
              const aboutUser = aboutDetails.about_user;

              return (
                <>
                <p className="m-0 text-start fs-7 text-capitalize">
            <img src={LocationIcon} className="icon" />
            location is here
          </p>
                <p className="fs-7 text-truncate-3 text-justify">{aboutUser}</p>
                </>
              );
            })}
          </Col>
        </Col>
      </Row>
      <Modal className="modal-dialog-scrollable" show={show} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>
            <Heading content="About Me" design="h4 my-auto" />
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Col sm={12} className="form-group mb-3">
              <label htmlFor="fullName" className="required">
                Full Name
              </label>
              <input
                type="text"
                placeholder="Enter your full name"
                className="form-control form-control-sm"
                // className={nameClasses}
                // onChange={nameChangeHandler}
                // onBlur={nameBlurHandler}
                // value={enteredName}
              />

              {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
            </Col>

            <Col sm={12} className="form-group mb-3">
              <label htmlFor="fullName" className="required">
                Contact Number
              </label>
              <input
                type="text"
                placeholder="Enter your Contact Number"
                className="form-control form-control-sm"
                // className={nameClasses}
                // onChange={nameChangeHandler}
                // onBlur={nameBlurHandler}
                // value={enteredName}
              />

              {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
            </Col>

            <Col sm={12} className="form-group mb-3">
              <label htmlFor="fullName" className="required">
                Email Address
              </label>
              <input
                plaintext 
                readOnly 
                defaultValue="email@example.com"
                className="form-control form-control-sm"
              />

              {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
            </Col>

            <Col sm={12} className="form-group mb-2">
                    <label htmlFor="gender" className="required">
                      Gender
                    </label>
                    <select
                      defaultValue={""}
                      aria-label="Gender"
                      className="form-select form-select-sm"
                      // className={locationClasses}
                      // onChange={locationChangeHandler}
                      // onBlur={locationBlurHandler}
                    >
                      <option value="">Male</option>
                      <option value="">Female</option>

                    </select>
                    {/* {locationInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please choose your company location.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputdob" className="required">
                    Date of Birth
                    </label>
                    <input
                      type="date"
                      id="inputdob"
                      placeholder="Your Birthday"
                      className="form-control form-control-sm"
                      // className={dobClasses}
                      // onChange={dobChangeHandler}
                      // onBlur={dobBlurHandler}
                      // value={enteredDob}
                    />
                    {/* {dobInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please select your birthdate.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="gender">
                      Nationality
                    </label>
                    <select
                      defaultValue={""}
                      aria-label="Gender"
                      className="form-select form-select-sm"
                      // className={locationClasses}
                      // onChange={locationChangeHandler}
                      // onBlur={locationBlurHandler}
                    >
                      <option value="">Malaysian</option>
                      <option value="">Others</option>

                    </select>
                    {/* {locationInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please choose your company location.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>


                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputabout">
                      Adress
                    </label>
                    <Form.Control
                      as="textarea"
                      placeholder="Enter your address"
                      className="form-control form-control-sm"
                      // className={aboutClasses}
                      // onChange={aboutChangeHandler}
                      // onBlur={aboutBlurHandler}
                      // value={enteredAbout}
                    />
                    {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>


                  <Col sm={12} className="form-group mb-2">
                    <label htmlFor="inputabout" className="required">
                      About
                    </label>
                    <Form.Control
                      as="textarea"
                      placeholder="Tell us about you"
                      className="form-control form-control-sm"
                      // className={aboutClasses}
                      // onChange={aboutChangeHandler}
                      // onBlur={aboutBlurHandler}
                      // value={enteredAbout}
                    />
                    {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={() => setShow(false)}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default About;
