import React, { Fragment } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import { Helmet, HelmetProvider } from "react-helmet-async";
import Heading from "../Components/Heading";

function Contact() {
  return (
    <Fragment>
      <HelmetProvider>
        <Helmet>
          <title> Contact | Zom - IN </title>
        </Helmet>
      </HelmetProvider>
      <div id="contact" className="contact my-2">
        <Container>
          <Row>
            <Col lg={10} md={9} sm={12} className="mx-auto">
              <Heading
                content="Need Our Assistance?"
                design="h4 fw-bold text-center my-3"
              />
              <Col sm={11} className="border rounded p-lg-5 p-md-3 p-3 mx-auto">
                <Form>
                <Col sm={12} className="form-group mb-3">
                    <label htmlFor="studyField" className="required">
                    Personal Name
                    </label>
                    <input
                      type="text"
                      placeholder="Enter the person-in-charge name"
                      className="form-control form-control-sm"
                    //   className={studyFieldClasses}
                    //   onChange={studyFieldChangeHandler}
                    //   onBlur={studyFieldBlurHandler}
                    //   value={enteredStudyField}
                    />

                    {/* {studyFieldInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your field of study.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

                  <Col sm={12} className="form-group mb-3">
                    <label htmlFor="studyField" className="required">
                    Email Address
                    </label>
                    <input
                      type="email"
                      placeholder="name@example.com"
                      className="form-control form-control-sm"
                    //   className={studyFieldClasses}
                    //   onChange={studyFieldChangeHandler}
                    //   onBlur={studyFieldBlurHandler}
                    //   value={enteredStudyField}
                    />

                    {/* {studyFieldInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your field of study.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

                  <Col sm={12} className="form-group mb-3">
                    <label htmlFor="studyField" className="required">
                    Contact Number
                    </label>
                    <input
                      type="text"
                      placeholder="01X-XXXXXXX"
                      className="form-control form-control-sm"
                    //   className={studyFieldClasses}
                    //   onChange={studyFieldChangeHandler}
                    //   onBlur={studyFieldBlurHandler}
                    //   value={enteredStudyField}
                    />

                    {/* {studyFieldInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in your field of study.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>

                  <Col sm={12} className="form-group mb-3">
                    <label htmlFor="inputabout" className="required">
                    What Can We Help You With?
                    </label>
                    <Form.Control
                      as="textarea"
                      placeholder="Tell us what you want to know more."
                      className="form-control form-control-sm"
                    //   className={aboutClasses}
                    //   onChange={aboutChangeHandler}
                    //   onBlur={aboutBlurHandler}
                    //   value={enteredAbout}
                    />
                    {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>
                  <Col sm={12} className="text-end mb-3">
                      <Button className=" btn btn-primary"> Submit</Button>
                  </Col>
                </Form>
              </Col>
            </Col>
            <Col lg={2} md={3} sm={12} className="mx-auto">
              <Heading content="sidebar" design="h5 border" />
            </Col>
          </Row>
        </Container>
      </div>
    </Fragment>
  );
}

export default Contact;
