import React, { useState, useEffect, Fragment } from "react";
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import {
  BiPencil,
  BiMinusCircle,
  BiXCircle,
  BiMessageCheck,
  BiPlus,
} from "react-icons/bi";

import { Col, Row, Modal } from "react-bootstrap";
import Heading from "../../../Heading";
import { toast } from "react-toastify";

function Skills() {
  const navigate = useNavigate();
  const [skills, setSkills] = useState([]);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
      Axios.get(
        `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/skills/`,
        {
          headers: {
            accessToken: localStorage.getItem("accessToken"),
          },
        }
      ).then((response) => {
        if (response.data.error) {
          return toast.error(
            <Fragment>
              <BiMinusCircle /> <span>{response.data.error}</span>
            </Fragment>
          );
        } else {
          setSkills(response.data);
        }
      });
    }
  }, []);

  const deleteSkill = (skillID) => {
    Axios.delete(
      `${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/skills/` +
        skillID,
      {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      }
    ).then((response) => {
      if (response.data.error) {
        return toast.error(
          <Fragment>
            <BiMinusCircle /> <span>{response.data.error}</span>
          </Fragment>
        );
      } else {
        return [
          toast.success(
            <Fragment>
              <BiMessageCheck /> <span>{response.data.success}</span>
            </Fragment>
          ),
          setSkills(
            skills.filter((skillsDetails) => {
              /* eslint-disable-next-line */
              return skillsDetails.id != skillID;
            })
          ),
        ];
      }
    });
  };

  return (
    <Row className="mx-auto border rounded p-2 my-2">
      <Col sm={12}>
        <Row>
          <Col xs={10} sm={10} className="text-start">
            <Heading content="Skills" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto">
              <BiPlus
                className="icon"
                type="button"
                data-bs-toggle="modal"
                data-bs-target="addSkill"
              />

<div
                  className="modal fade"
                  id="addSkill"
                  data-bs-backdrop="static"
                  data-bs-keyboard="false"
                  tabIndex="-1"
                  aria-labelledby="staticBackdropLabel"
                  aria-hidden="true"
                >
                  <div className="modal-dialog modal-dialog-scrollable">
                    <div className="modal-content">
                      <div className=" modal-header">
                        <Heading content="Delete Skills" design="h4 my-auto" />
                        <button
                          type="button"
                          id="closeButton"
                          className="btn-close"
                          data-bs-dismiss="modal"
                          aria-label="Close"
                        ></button>
                      </div>
                      <div class="modal-body">
                        <p>Are you sure want to delete {skill}?</p>
                      </div>
                      <div class="modal-footer">
                        <button
                          type="button"
                          class="btn btn-secondary"
                          data-bs-dismiss="modal"
                        >
                          Close
                        </button>
                        <button
                          type="button"
                          class="btn btn-primary"
                          onClick={() => deleteSkill(skillID)}
                          data-bs-dismiss="modal"
                        >
                          Save Changes
                        </button>
                      </div>
                    </div>
                  </div>
                </div>

            </p>
          </Col>
        </Row>
      </Col>
      <Col sm={12}>
        <div className="d-flex flex-wrap">
          {skills.map((skillsDetails) => {
            const skillID = skillsDetails.id;
            const skill = skillsDetails.skill;
            return (
              <>
                <p
                  key={skillID}
                  className="badge rounded-pill alert-primary fw-normal text-dark m-2 fs-7"
                >
                  {skill}{" "}
                  <BiXCircle
                    type="button"
                    data-bs-toggle="modal"
                    data-bs-target={`#createPostModal${skillID}`}
                    //
                  />
                </p>

                <div
                  className="modal fade"
                  id={`createPostModal${skillID}`}
                  data-bs-backdrop="static"
                  data-bs-keyboard="false"
                  tabIndex="-1"
                  aria-labelledby="staticBackdropLabel"
                  aria-hidden="true"
                >
                  <div className="modal-dialog modal-dialog-scrollable">
                    <div className="modal-content">
                      <div className=" modal-header">
                        <Heading content="Delete Skills" design="h4 my-auto" />
                        <button
                          type="button"
                          id="closeButton"
                          className="btn-close"
                          data-bs-dismiss="modal"
                          aria-label="Close"
                        ></button>
                      </div>
                      <div class="modal-body">
                        <p>Are you sure want to delete {skill}?</p>
                      </div>
                      <div class="modal-footer">
                        <button
                          type="button"
                          class="btn btn-secondary"
                          data-bs-dismiss="modal"
                        >
                          Close
                        </button>
                        <button
                          type="button"
                          class="btn btn-primary"
                          onClick={() => deleteSkill(skillID)}
                          data-bs-dismiss="modal"
                        >
                          Save Changes
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </>
            );
          })}
        </div>
      </Col>
    </Row>
  );
}

export default Skills;
