import React, { Fragment, useEffect, useState, useContext } from "react";
import { AuthContext } from "../../Helpers/AuthContext";
import Axios from "axios";

import { Row, Col, Button, Form } from "react-bootstrap";
import { BiTime } from "react-icons/bi";

import Ads from "../Ads";
import Profile from "../Profile";
import Name from "../Name";
import Heading from "../Heading";

function User() {
  const { authState, setAuthState } = useContext(AuthContext);
  const [interviewList, setInterviewList] = useState([]);
  const [rescheduleModal, setRescheduleModal] = useState(false);

  useEffect(() => {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/userInterview/list`, {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }).then((response) => {
      setInterviewList(response.data);
      console.log(response.data);
    });
  }, []);

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <div>
        <Row className="mx-auto p-5">
          <Col lg={10} md={10} sm={12}>
            {interviewList.map((interviewDetails, key) => {
              const rescheduleID = interviewDetails.id;
              const employerID = interviewDetails.bp_id;
              const jobTitle = interviewDetails.job_title;
              const interviewerName = interviewDetails.interviewerName;
              const interviewRemarks = interviewDetails.interviewRemarks;
              const interviewLink = interviewDetails.interviewVenueOrVideo;
              const interviewingTime = new Date(interviewDetails.interviewTime);
              const interviewTime = interviewingTime
                .toLocaleDateString("en-GB", {
                  day: "numeric",
                  month: "short",
                  year: "numeric",
                  hour: "2-digit",
                  minute: "2-digit",
                  hour12: true,
                })
                .replace(/ /g, " ");

              return (
                <Row className="mx-auto my-3 p-3 border-bottom">
                  <Col sm={2} className="d-none d-md-none d-lg-block">
                    <Profile
                      id={employerID}
                      design="rounded-circle"
                      width="100"
                      height="100"
                    />
                  </Col>
                  <Col xs={12} sm={8}>
                    <p className="m-0">{jobTitle}</p>
                    <p className="m-0">
                      <Name id={employerID} />
                    </p>
                    <p className="m-0">Intervierew Name: {interviewerName}</p>
                    <p className="m-0">
                      <BiTime className="icon" />
                      {interviewTime}
                    </p>
                    <a
                      href={interviewLink}
                      target="_blank"
                      className="m-0 text-decoration-none"
                    >
                      {interviewLink}
                    </a>
                    <p>Remarks: {interviewRemarks}</p>
                  </Col>
                  <Col sm={2} className="text-center m-auto">
                    <Button
                      variant="warning"
                      onClick={() => setRescheduleModal(true)}
                      data-bs-toggle="modal"
                      data-bs-target={`#col-${rescheduleID}`}
                    >
                      Re-Schedule
                    </Button>
                  </Col>

                  <div
                    className="modal fade"
                    id={`col-${rescheduleID}`}
                    data-bs-backdrop="static"
                    data-bs-keyboard="false"
                    tabIndex="-1"
                    aria-labelledby="staticBackdropLabel"
                    aria-hidden="true"
                  >
                    <div className="modal-dialog modal-dialog-scrollable">
                      <div className="modal-content">
                        <div className=" modal-header">
                          <Heading
                            content="Request Interview Re-Schedule"
                            design="h5"
                          />
                          <button
                            type="button"
                            id="closeButton"
                            className="btn-close"
                            data-bs-dismiss="modal"
                            aria-label="Close"
                          ></button>
                        </div>
                        <Form
                        // onSubmit={
                        //   requestWithdrawFormSubmissionHandler
                        // }
                        >
                          <div className="modal-body">
                            <Col sm={12} className="form-group mb-3">
                              <label
                                htmlFor="intialTimeAndDate"
                                className="required"
                              >
                                Initial Time and Date
                              </label>
                              <input
                                plaintext
                                readOnly
                                defaultValue={interviewTime}
                                className="form-control-plaintext form-control-sm"
                              />
                            </Col>

                            <Col sm={12} className="form-group mb-3">
                              <label htmlFor="proposedTimeDate" className="required">
                                Proposed Time and Date
                              </label>
                              <input
                                type="datetime-local"
                                placeholder="Enter your Proposed Time and Date"
                                autoComplete="off"
                                className="form-control form-control-sm"
                                // className={jobTitleClasses}
                                // onChange={jobTitleChangeHandler}
                                // onBlur={jobTitleBlurHandler}
                                // value={enteredJobTitle}
                              />

                              {/* {jobTitleInputHasError && (
            <Form.Control.Feedback type="invalid">
              Please fill in the job titl e.
            </Form.Control.Feedback>
          )} */}
                            </Col>

                            <Col sm={12} className="form-group mb-2">
                              <label htmlFor="inputremarks" className="required">
                                Remarks
                              </label>
                              <Form.Control
                                as="textarea"
                                placeholder="Your reason for rescheduling"
                                className="form-control form-control-sm"

                                // className={aboutClasses}
                                // onChange={aboutChangeHandler}
                                // onBlur={aboutBlurHandler}
                                // value={enteredAbout}
                              />
                              {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                            </Col>
                          </div>
                          <div className="modal-footer">
                            <Button
                              variant="warning"
                              type="submit"
                              // disabled={!requestWithdrawFormIsValid}
                            >
                              Re-Schedule
                            </Button>
                          </div>
                        </Form>
                      </div>
                    </div>
                  </div>
                </Row>
              );
            })}
          </Col>
          <Col lg={2} md={2} sm={12} className="px-1 py-3 my-1 mx-auto">
            <Ads />
          </Col>
        </Row>
      </div>
    </AuthContext.Provider>
  );
}

export default User;
