import React, { useState } from "react";
import { BiChevronLeft } from "react-icons/bi";
import { FcCamera } from "react-icons/fc";
import { BsFillEnvelopeFill } from "react-icons/bs";
import { FiPhoneCall } from "react-icons/fi";

import { Col, Row } from "react-bootstrap";

import Heading from "../Heading";
import UploadImgModal from "./UploadImgModal";

function EmployerProfile({ setshowSubContentClick }) {
    const [uploadImgModal, setUploadImgModal] = useState(false);
  return (
    <div>
      <h1
        onClick={() => setshowSubContentClick(0)}
        className="me-2 d-block d-sm-none"
      >
        <BiChevronLeft className="d-inline" />
        <Heading content="My Profile" design="h1 d-inline-block" />
      </h1>
      <Col lg={10} md={12} sm={12} className="mx-auto my-2 border">
        <Col sm={12} className="position-relative">
          <FcCamera
            size={28}
            className="position-absolute top-0 end-0 me-4 mt-2"
            role="button"
            onClick={()=> setUploadImgModal(true)}
          />
          <UploadImgModal 
              show={uploadImgModal}
              onHide={() => setUploadImgModal(false)}
          />

          <img
            src="https://www.itoutcomes.com/wp-content/uploads/2016/10/IT-consultancy.jpg"
            class="img-fluid coverImage border border-1"
          />


          <img
            src="https://www.betterteam.com/images/it-consultant-job-description-4088x2725-2020123.jpeg?crop=1:1,smart&width=1200&dpr=2"
            class=" ms-2 align-self-center roundedProfile position-absolute top-50 end-0 translate-middle-x translate-middle-y border border-1"
role="button"
onClick={()=> setUploadImgModal(true)}
          />

          <Row className="border border-top-0 mx-0 rounded">
            <Col sm={12} md={9} className="mx-1 p-2">
                <Heading content="igris consultancy" design="h4 d-inline" />
                <span className="fw-normal fs-6">(Consultancy)</span>
              <p class="m-0 p-0">Angeline Ann Davis</p>
              <p class="m-0 p-0"><BsFillEnvelopeFill className="icon"/>igrisconsultancy@gmail.com</p>
              <p class="m-0 p-0"><FiPhoneCall className="icon"/>1234568932</p>
            </Col>
          </Row>
        </Col>

      </Col>
    </div>
  );
}

export default EmployerProfile;
