                      editor={ClassicEditor}
                      config={{
                        placeholder: "Share your thoughts...",
                        removePlugins: [
                          "Heading",
                          "Essentials",
                          "Italic",
                          "Bold",
                          "imageUpload",
                          "EasyImage",
                          "ImageUpload",
                          "MediaEmbed",
                          "BlockQuote",
                          "List",
                          "Indent",
                          "Table",
                          // "link"
                        ],
                      }}
                      data={postText}
                      onChange={(event, editor) => {
                        const data = editor.getData();
                        setPostText(data);
                      }}
                    />

