                  <Col xs={10} sm={10} className="">
                    <Row>
                      <Col sm={12}>
                        <p className="my-auto fw-bold">
                          {postCompany === 1 ? (
                            <a
                              href={postCompanyLink}
                              target="_blank"
                              rel="noreferrer"
                              className="text-decoration-none text-dark"
                            >
                              {postCompanyName}
                            </a>
                          ) : (
                            <a
                              href="/home"
                              className="text-decoration-none text-dark"
                            >
                              {" "}
                              {postUserName}
                            </a>
                          )}
                        </p>
                      </Col>
                      <Col sm={12}>
                        <p className="text-muted fw-light fs-7">
                          <BsClockFill size={12} className="me-1" />
                          {/* 10th June 2021 5:14 pm */}
                          {postTime}
                        </p>
                      </Col>
                    </Row>
                  </Col>
                </Row>
              </Col>
              <Col xs={2} sm={2} className="text-end">
                <div className="dropdown">
                  <button
                    className="btn shadow-none"
                    type="button"
                    id="dropdownMenuButton1"
                    data-bs-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <BsThreeDotsVertical />
                  </button>
                  <ul
                    className="dropdown-menu dropdown-menu-end"
                    aria-labelledby="dropdownMenuButton1"
                  >
                    {postOwnerID === userID && (
                      <>
                        <li>
                          <button
                            type="button"
                            className="btn btn-link dropdown-item"
                            onClick={() =>editPost(postID,postText, postImageName, postVideoName)}
                          >
                            <BsPencilSquare className="icon" />
                            Edit
                          </button>

{
  editPostModal &&  <Modal
                          show fade
                          className="editPosthere"
        backdrop="static"
        keyboard={false}
      >
         <div class="modal-header">
         <Heading content="Edit Post" design="h5 " />
        <button type="button" class="btn-close" data-bs-dismiss="modal" onClick={handleCloseEditPostModal} aria-label="Close"></button>
      </div>
        <Modal.Body>
        <CKEditor
                      editor={ClassicEditor}
                      config={{
                        placeholder: "Share your thoughts...",
                        removePlugins: [
                          "Heading",
                          "Essentials",
                          "Italic",
                          "Bold",
                          "imageUpload",
                          "EasyImage",
                          "ImageUpload",
                          "MediaEmbed",
                          "BlockQuote",
                          "List",
                          "Indent",
                          "Table",
                          // "link"
                        ],
                      }}
                      data={postEditText}
                      onChange={(event, editor) => {
                        const data = editor.getData();
                        setPostEditText(data);
                      }}
                    />
<hr />

<Row>
  <Col  xs={6} sm={6} className="p-2 text-end">
<span className=" badge fw-bold border-0 text-dark p-3 fs-6 position-relative overflow-hidden themeColorUser inputPostFile"><FcCamera className="icon" />Photos
<input
                            type="file"
                            accept="image/*"
                            style={editFileStyle}
                            onChange={imageEditChange}
                          />

</span>
  </Col>
  <Col xs={6}
                        sm={6}
                        className="p-2 text-start border-start">
<span className="badge fw-bold border-0 text-dark p-3 fs-6 position-relative overflow-hidden themeColorUser inputPostFile">
                          <FcVideoCall className="icon" /> Videos
                          <input
                            type="file"
                            accept="video/*"
                            style={editFileStyle}
                            // onChange={videoChange}
                          />
