  </Col>
</Row>

{postEditImage  && (
                      <div className="m-2 w-75 mx-auto border border-5 themeColorUser rounded imgPostPreview">
                        <div className="position-relative">
                          <div className="position-absolute top-0 start-100 translate-middle">
                            <button
                              onClick={removeEditSelectedImage}
                              className="bg-transparent rounded-circle border-0 position-relative"
                            >
                              <BsXCircleFill
                                size={21}
                                className="bg-white rounded-circle"
                              />
                            </button>
                          </div>
                        </div>
                        <img
                          src={URL.createObjectURL(postEditImage)}
                          className="w-100"
                          alt=" Upload"
                        />
                      </div>
                    )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="warning">Edit Post</Button>
        </Modal.Footer>
      </Modal>
}

                        </li>
                        <li>
                          <button
                            type="button"
                            className="btn btn-link dropdown-item"
                            value={postID}
                            onClick={deletePost}
                          >
                            <BsTrash className="icon" /> Delete
                          </button>
                        </li>
                        <li>
                          <hr className="dropdown-divider" />
                        </li>
                      </>
                    )}
                    <li>
                      <Report
                        postID={postID}
                        userID={userID}
                        postOwnerID={postOwnerID}
                      />
                    </li>
                  </ul>
                </div>
