                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="inputdob" className="required">
                            Date of Birth
                          </label>
                          <input
                            type="date"
                            id="inputdob"
                            placeholder="Your Birthday"
                            className="form-control form-control-sm"
                            // className={dobClasses}
                            onChange={(e) => setDob(e.target.value)}
                            defaultValue={[dob || enteredDob]}
                          />
                          {/* {dobInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please select your birthdate.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>
                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="gender">Nationality</label>
                          <select
                            aria-label="Gender"
                            className="form-select form-select-sm"
                            // className={locationClasses}
                            onChange={(e) => setNationality(e.target.value)}
                            defaultValue={[nationality || enteredNationality]}
                          >
                            <option value="">Please Choose One</option>
                            <option value="Malaysian">Malaysian</option>
                            <option value="Others">Others</option>
                          </select>
                          {/* {locationInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please choose your company location.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="inputAddress">Adress</label>
                          <Form.Control
                            as="textarea"
                            placeholder="Enter your address"
                            className="form-control form-control-sm"
                            // className={aboutClasses}
                            onChange={(e) => setAddress(e.target.value)}
                            value={[address || enteredAddress]}
                          />
                          {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>

                        <Col sm={12} className="form-group mb-2">
                          <label htmlFor="inputabout" className="required">
                            About
                          </label>
                          <Form.Control
                            as="textarea"
                            placeholder="Tell us about you"
                            className="form-control form-control-sm"
                            // className={aboutClasses}
                            onChange={(e) => setAboutUser(e.target.value)}
                            value={[aboutUser || enteredAbout]}
                          />
                          {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                        </Col>


                          </div>
                          <div className="modal-footer">
                            <button
                              type="button"
                              className="btn btn-secondary"
                              data-bs-dismiss="modal"
                            >
                              Close
                            </button>
                            <button
                              type="submit"
                              className="btn btn-primary"
                              data-bs-dismiss="modal"
                              // disabled={!workExpFormIsValid}
                            >
                              Save Changes
                            </button>
                          </div>
                        </Form>
                      </div>
                    </div>
                  </div>
                </>
              );
            })}
          </Col>
        </Col>
      </Row>
    </>
  );
