import React, { useState, useEffect }  from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
// import { Helmet } from 'react-helmet';
import Axios from "axios";
import routes from "./Config/routes";
import NotFound from "./Pages/NotFound";
import Logo from './Components/Logo';
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "react-datepicker/dist/react-datepicker.css";
import "react-bootstrap-typeahead/css/Typeahead.css";

import "./App.css";

import { AuthContext } from './Helpers/AuthContext';


import { Navbar, Container, Nav, Button } from 'react-bootstrap';

function App() {

  const [authState, setAuthState] = useState({
    id: "",
    admin: "",
    company: "",
    status: false,
    attempt: "",
  });

  useEffect(() => {
    Axios
    .get(`${process.env.REACT_APP_SERVER_DOMAIN}/auth/authenticate`, { 
      headers: {
        accessToken: localStorage.getItem("accessToken"),
},
  })
  .then ((response) => {
      if (response.data.error) {
        setAuthState({...authState, status: false});
      } else {
        setAuthState({
          id: response.data.id,
          admin: response.data.admin,
          company: response.data.company,
          attempt: response.data.attempt,
          status: true,
        });
      }
    });


  });

  const logout = () => {
        localStorage.removeItem("accessToken");
        setAuthState({
          id: "",
          admin: "",
          company: "",
          attempt: "",
          status: false,
        });
  };

  return (
    <div>
        <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
            <Navbar collapseOnSelect bg="white" variant="light" expand="lg" sticky="top" className="border-bottom">
                <Container>
                    <Navbar.Brand href="/">
                        <Logo />
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link href="/partner">Partners</Nav.Link>
                            <Nav.Link href="/opportunity">Opportunity</Nav.Link>
                            <Nav.Link href="whatshappening">What's Happening</Nav.Link>
                            <Nav.Link href="#features">Spice-IN</Nav.Link>
                            <Nav.Link href="#pricing">About Us</Nav.Link>
                            <Nav.Link href="/basicinfo">Testing</Nav.Link>

                        </Nav>
      {!authState.status ?

                        <Nav>
                            <Button variant="outline-primary" className="m-1" href="/SignUp">Sign Up</Button>
                            <Button variant="outline-success" className="m-1" href="/employer" >For Employer</Button>
                        </Nav>
                        :
                        <Nav>
                            <Button variant="outline-primary" className="m-1" onClick={logout}>Log Out</Button>
                        </Nav>
      }
      <h1>{authState.id}</h1>

                    </Navbar.Collapse>
                </Container>
            </Navbar>


        </Router>

      <Router>
        <Switch>
          {routes.map(({ path, component, name }) => {
            return <Route exact path={path} key={name} component={component} />;
          })}

          <Route component={NotFound} />
        </Switch>


        <ToastContainer
          position="bottom-left"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
        />
      </Router>
      </AuthContext.Provider>

    </div>
  );
}

export default App;
