import React, { useState } from "react";

import { BsFlag } from "react-icons/bs";
import { Form, Modal, Button } from "react-bootstrap";

import Heading from "../Heading";

function Report(props) {
  const postID = props.postID;
  const userID = props.userID;

  const reportPost = (event) => {
    alert(postID);
  };

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [reportInput, setReportInput] = useState("");

  let reportFormIsValid = false;

  if(reportInput !== "") {
    reportFormIsValid = true;
  }

  const reportFormSubmissionHandler = event => {
    event.preventDefault();

    if(reportInput === "") {
        return;
    } else {
        alert("submit")
    }

    setReportInput("");
  };



  return (
    <>
      <button
        type="button"
        className="btn btn-link dropdown-item"
        onClick={handleShow}
      >
        <BsFlag className="icon" /> Report
      </button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Heading content="Report" design="h5" />
        </Modal.Header>
        <Form onSubmit={reportFormSubmissionHandler}>
        <Modal.Body>
          <p className=" fw-bold">Why are you reporting this?</p>

          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost1"
              value={[
                reportInput === "Suspicious, spam or fake" || "Suspicious, spam or fake",
                        ]}
            />
            <label class="form-check-label" for="reportPost1">
              Suspicious, spam or fake
            </label>
          </div>
          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost2"
            />
            <label class="form-check-label" for="reportPost2">
              Harassment or hateful speech
            </label>
          </div>

          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost3"
            />
            <label class="form-check-label" for="reportPost3">
              Violence or physical harm
            </label>
          </div>

          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost4"
            />
            <label class="form-check-label" for="reportPost4">
              Adult Content
            </label>
          </div>

          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost5"
            />
            <label class="form-check-label" for="reportPost5">
              Intellectual property infringement or defamation
            </label>
          </div>

          <div class="form-check">
            <input
              class="form-check-input"
              type="radio"
              name="reportPost"
              id="reportPost6"
            />
            <label class="form-check-label col-12" for="reportPost6">
                <input 
                    className="form-control form-control-sm"
                    type="text" 
                    name="reportPost"
                    placeholder="Other reason"
                    />
            </label>
          </div>


        </Modal.Body>
        <Modal.Footer>
          <Button 
            typr="submit"
            variant="danger"
            disabled={!reportFormIsValid}
            >Report</Button>
        </Modal.Footer>
        </Form>
      </Modal>
    </>
  );
}

export default Report;
