import React, { Fragment, useState, useEffect } from 'react';
// import { Redirect } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import Axios from 'axios';

//import Components
import Header from '../Components/Header';
import Heading from '../Components/Heading';

//import GOOGLE and FACEBOOK
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';

//Icon
import { Button, Form } from 'react-bootstrap';
import { BiError, BiMessageCheck } from "react-icons/bi";

//Toast
import { toast } from 'react-toastify';

//Custom Style
import '../Styles/signup.css';




function SignUp() {

    //SignUp form or LogIn form
    const [userIsRegistered, setLogin] = useState(false);

    function getLogin() {
        setLogin(!userIsRegistered);
    }

    //SignUp Form
    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [contactNumber, setContactNumber] = useState("");
    const [password, setPassword] = useState("");

    //LogIn Form
    const [emailConf, setEmailConf] = useState("");
    const [passwordConf, setPasswordConf] = useState("");

    //LogIn Status
    const [loginStatus, setLoginStatus] = useState(false);

    Axios.defaults.withCredentials = true;
    const signup = (event) => {
        if (fullName && email && contactNumber && password) {
            Axios.post('http://localhost:3001/auth/signup', {
                fullName: fullName,
                email: email,
                contactNumber: contactNumber,
                password: password
            })
                .then((response) => {
                    if (response.data === 'Email sent successfully') {
                        return toast.success(
                            <Fragment>
                                <BiMessageCheck />
                                <span>Email sent successfully!</span>
                            </Fragment>);

                    } else if (response.data === 'Email Exist!') {
                        return toast.error(
                            <Fragment>
                                <BiError />
                                <span>This Email already exists!</span>
                            </Fragment>
                        );
                    } else if (response.data === 'Invalid Email!') {
                        return toast.error(
                            <Fragment>
                                <BiError />
                                <span>This Email is Invalid!</span>
                            </Fragment>
                        );
                    } else {
                        return toast.error(
                            <Fragment>
                                <BiError />
                                <span>System is Error. Please Try Again!</span>
                            </Fragment>
                        );
                    }
                });
        };
        event.preventDefault();
        setEmail("");
        setFullName("");
        setContactNumber("");
        setPassword("");
    };

    //LogIn Form
    const login = (event) => {
        if (emailConf && passwordConf) {
            Axios.post('http://localhost:3001/auth/login', {
                email: emailConf,
                password: passwordConf
            })
                .then((response) => {
                    
                    if (!response.data.auth) {
                        if (response.data.message === 'Please Verify your account!') {
                            return toast.warn(
                                <Fragment>
                                    <BiError />
                                    <span>Please Verify your account!</span>
                                </Fragment>
                            );
                        } else if (response.data.message === 'Wrong email / password!'){
                            return toast.error(
                                <Fragment>
                                    <BiError />
                                    <span>Wrong email / password!</span>
                                </Fragment>
                            );
                        } else if (response.data.message === 'Email does not exist!') {
                            return toast.error(
                                <Fragment>
                                    <BiError />
                                    <span>Email does not exist!</span>
                                </Fragment>
                            );
                        } else {
                            return toast.error(
                                <Fragment>
                                    <BiError />
                                    <span>System is Error. Please Try Again!</span>
                                </Fragment>
                            );
                        }
                    } else {
                        localStorage.setItem("token", response.data.token)
                        setLoginStatus(true);
                    }
                });
        };
        event.preventDefault();
        setEmailConf("");
        setPasswordConf("");
    };

    useEffect(() => {
        Axios.get("http://localhost:3001/auth/login").then((response) => {
            if (response.data.loggedIn === true) {
                setLoginStatus(response.data.user[0].id);
            }
          });
    }, []);

    const userAuthenticated = () => {
        Axios.get("http://localhost:3001/auth/isUserAuth", { 
            headers: {
                "x-access-token": localStorage.getItem("token"),
            },
        }).then ((response) => {
            console.log(response);
        });
    };

    //GOOGLE
    const responseGoogle = (response) => {
        Axios.post('http://localhost:3001/auth/google', {
            fullName: response.profileObj.name,
            email: response.profileObj.email,
            googleId: response.profileObj.googleId,
            // imageUrl: response.profileObj.imageUrl
        })
            .then((response) => {
                if (response.data === 'first time reg') {
                    return toast.success(
                        <Fragment>
                            <BiMessageCheck />
                            <span>first time reg</span>
                        </Fragment>);

                } else if (response.data === 'Login to next page') {
                    return toast.success(
                        <Fragment>
                            <BiMessageCheck />
                            <span>Login to next page</span>
                        </Fragment>);
                } else {
                    return toast.error(
                        <Fragment>
                            <BiError />
                            <span>System is Error. Please Try Again!</span>
                        </Fragment>
                    );
                }
            });
    };

    //FACEBOOK
    const responseFacebook = (response) => {
        // console.log(response);
        // console.log(response.name);
        // console.log(response.email);
        // console.log(response.id);
        // //console.log(response.picture.data.url);
    }


    return (
        <Fragment>
            <Helmet>
                <title>Sign Up</title>
            </Helmet>
            <Header />
            
            <div id="signUp" className="col-11 col-md-9 text-center">
                <div className="row">
                    <div className="col-12 col-md-6">
                        {userIsRegistered ?
                            <div>

                                <Heading content="Log In Now!" />
                                <Form>
                                    <Form.Group className="mb-3" controlId="exampleForm.ControlEmail">
                                        <Form.Control
                                            size="sm"
                                            type="email"
                                            required
                                            name="email"
                                            placeholder="Email Address"
                                            value={emailConf}
                                            onChange={(e) => {
                                                setEmailConf(e.target.value);
                                            }}
                                        />
                                    </Form.Group>
                                    <Form.Group className="mb-3" controlId="exampleForm.ControlPassword">
                                        <Form.Control
                                            size="sm"
                                            type="password"
                                            required
                                            name="password"
                                            placeholder="Password"
                                            value={passwordConf}
                                            onChange={(e) => {
                                                setPasswordConf(e.target.value);
                                            }}
                                        />
                                    </Form.Group>
                                    <Button variant="primary" type="submit" onClick={login}>
                                        Log In
                                    </Button>
                                </Form>
                            </div>

                            :
                            <div>
                                <Heading content="Sign Up Now!" />


                                <Form id="signUpForm">
                                    <Form.Group className="mb-3" controlId="exampleForm.ControlFullName">
                                        <Form.Control
                                            size="sm"
                                            type="text"
                                            required
                                            name="fullName"
                                            placeholder="Full Name"
                                            value={fullName}
                                            onChange={(e) => {
                                                setFullName(e.target.value);
                                            }}
                                        />
                                    </Form.Group>
                                    <Form.Group className="mb-3" controlId="exampleForm.ControlEmail">
                                        <Form.Control
                                            size="sm"
                                            type="email"
                                            required
                                            name="email"
                                            placeholder="Email Address"
                                            value={email}
                                            onChange={(e) => {
                                                setEmail(e.target.value);
                                            }}
                                        />
                                    </Form.Group>

                                    <Form.Group className="mb-3" controlId="exampleForm.ControlContactNumber">
                                        <Form.Control
                                            size="sm"
                                            type="tel"
                                            required
                                            name="contactNumber"
                                            placeholder="Contact Number"
                                            value={contactNumber}
                                            onChange={(e) => {
                                                setContactNumber(e.target.value);
                                            }}
                                        />
                                    </Form.Group>
                                    <Form.Group className="mb-3" controlId="exampleForm.ControlPsssword">
                                        <Form.Control
                                            size="sm"
                                            type="password"
                                            required
                                            name="password"
                                            placeholder="Password"
                                            value={password}
                                            onChange={(e) => {
                                                setPassword(e.target.value);
                                            }}

                                        />
                                    </Form.Group>
                                    <div className="mb-3">
                                        <label>By clicking sign up, you agree to Zom-In's <a className="text-decoration-none" href="https://github.com" >Privacy Policy</a> and <a className="text-decoration-none" href="https://github.com">Term of Use</a></label>
                                    </div>
                                    <Button variant="primary" type="submit" onClick={signup}>
                                        Sign Up
                                    </Button>
                                </Form>
                            </div>

                        }
                        {loginStatus && (<Button variant="success" onClick={userAuthenticated}>check on authenticate</Button>)}
                        <div className="row">
                            <div className="col">
                                <div className=" text-start">

                                    <Button variant="link">{userIsRegistered ? "Forget Password?" : null}</Button>
                                </div>
                            </div>
                            <div className="col">
                                <div className=" text-end">
                                    <Button variant="link" onClick={getLogin} >{userIsRegistered ? "Don't have an account?" : "Already have an account?"} </Button>
                                </div>
                            </div>
                        </div>
                    </div>
                    

                    <div className="col-12 col-md-6 divider ">
                        <Heading content="Log In with Social Network" />
                        <div className="mb-5 d-grid gap-3 socialbox">
                            <GoogleLogin
                                clientId="300636281235-n81sjr9a8p5fk392rq658p34p2nl3dq7.apps.googleusercontent.com"
                                buttonText="Login with Google"
                                onSuccess={responseGoogle}
                                onFailure={responseGoogle}
                                cookiePolicy={'single_host_origin'}
                            />
                        </div>
                        <div className="mb-5 d-grid gap-3 socialbox">
                            <FacebookLogin
                                appId="974198170064539"
                                autoLoad={true}
                                fields="name,email,picture"
                                callback={responseFacebook}
                                icon="fa-facebook" />
                        </div>
                    </div>
                </div>
            </div>
        </Fragment>
    )
}

export default SignUp;
