import React from "react";
// { Fragment, useContext }
// import Axios from "axios";
// import { useNavigate } from "react-router-dom";
// import { AuthContext } from "../../Helpers/AuthContext";

import { Modal, Col, Form, Button } from "react-bootstrap";
// import { BiMessageCheck, BiMinusCircle } from "react-icons/bi";

// //Toast
// import { toast } from "react-toastify";

import Heading from "../Heading";

function JobStatus(props) {
  const grabID = props.grabid;
  const statusCode = props.status;
  const cddName = props.cddname;

  let statusInfo;
  let status;
  switch (statusCode) {
    case "0":
      status = "Pending";
      statusInfo = "Pending";
      break;
    case "1":
      status = "Reject";
      statusInfo = "Rejecting";
      break;
    case "2":
      status = "Hired";
      statusInfo = "Hiring";
      break;
    case "3":
      status = "Shortlisted";
      statusInfo = "Shortlisting";
      break;
    default:
      statusInfo = "Pending";
  }

  const aboutClasses = "form-control form-control-sm";
  //  aboutInputHasError
  // ? "form-control form-control-sm is-invalid"
  // : "form-control form-control-sm";


  // Hire Process
  const hireCandidate = (id) => {
    let grabID = [id];
    console.log(grabID);
  }

  return (
    <div>
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          {status === "Reject" ? (
            <Heading content="Reason for Rejection" design="h5" />
          ) : (
            <Heading content={`${statusInfo} this candidate, ${cddName}?`} design="h5" />
          )}
        </Modal.Header>
        <Modal.Body>
          {status === "Reject" && (
            <>
              <Form>
                <Col sm={12} className="form-group mb-2">
                  <label htmlFor="inputabout" className="required">
                    Would appreciate if you could share some feedback about this
                    candidate
                  </label>

                  <Form.Control
                    as="textarea"
                    placeholder="Share some feedback here"
                    className={aboutClasses}
                    // onChange={aboutChangeHandler}
                    // onBlur={aboutBlurHandler}
                    // value={enteredAbout}
                  />
                  {/* {aboutInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Tell us about you.
                      </Form.Control.Feedback>
                    )} */}
                </Col>

                <Col sm={12} className="form-group mb-2">
                  <input
                    className="form-check-input me-2"
                    type="checkbox"
                    value=""
                    id="flexCheckChecked"
                  />
                  <label htmlFor="inputabout">Display to the candidate</label>
                </Col>
              </Form>
            </>
          )}

          {status === "Hired" && (
            <>
              <p>
                Thanks for providing a career opportunity to this candidate,{" "}
                {cddName}!
              </p>
              <p>
                By clicking the Confirm button, a message will be sent to the
                candidate to notify on your hiring.
              </p>
              <p>
                We highly recommend you to contact the candidate within 3
                working days to proceed with your regular hiring process.
              </p>

              <p>
                Should you need any further assistance, please don't hesitate to
                contact us at{" "}
                <a href="admin@zom-in.com" className="text-decoration-none">
                  admin@zom-in.com
                </a>
                . Thanks!
              </p>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          {status === "Reject" && (
            <>
              <Button variant="danger" href="/profile">
                Reject this Candidate
              </Button>
            </>
          )}

          {status === "Hired" && (
            <>
              <Button variant="outline-success" onClick={() => hireCandidate(grabID)}>Confirm</Button>
            </>
          )}
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default JobStatus;
