import React, { useState, useEffect, Fragment } from "react";
import Axios from "axios";

import UserImg from "../../Assests/Images/user/sha.jpeg";
import Report from "./Report";

import { Container, Row, Col, Collapse } from "react-bootstrap";
import {
  BsClockFill,
  BsThreeDotsVertical,
  BsFlag,
  BsTrash,
  BsPencilSquare,
} from "react-icons/bs";
import { FaThumbsUp, FaComments, FaShare } from "react-icons/fa";
import { BiMessageCheck } from "react-icons/bi";

//Toast
import { toast } from "react-toastify";



function Posts(props) {
  const userID = props.userID;
  const postOwnerID = props.postOwnerID;
  const sharePostLink = props.sharePostLink;

  if (props.postCompany === 1) {
    var postCompanyLink =
      "/partner/" +
      props.postCompanyName.toLowerCase().trim().split(/\s+/).join("-");
  }

  const [viewProfile, setViewProfile] = useState("avatar/male.svg");

  useEffect(() => {
    Axios.get(
      `${process.env.REACT_APP_SERVER_DOMAIN}/home/userProfile/` + postOwnerID
    ).then((response) => {
      const profileDetail1 = response.data[0];
      const profileDetail2 = response.data[1];

      var profile;

      if (profileDetail1.length === 0) {
        if (profileDetail2[0].gender === "Male") {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/male.svg`;
          setViewProfile(profile);
        } else {
          profile = `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/avatar/female.svg`;
          setViewProfile(profile);
        }
      } else {
        profile =
          `${process.env.REACT_APP_SERVER_DOMAIN}/public/Assests/Images/user/` +
          profileDetail1[0].profile_photo;
        setViewProfile(profile);
      }
    });
  }, [postOwnerID]);

  function copyPostLink(sharePostLink) {
    navigator.clipboard.writeText(sharePostLink);
    return toast.dark(
      <Fragment>
        <BiMessageCheck />
        <span>Copied to the clipboard!</span>
      </Fragment>
    );
  }

  const [open, setOpen] = useState(false);

  const test = (event) => {
    alert("Click on button Option here");
  };




  return (
    <Container id={props.postID} className="border p-2 mb-3">
      <Row className="mb-3">
        <Col xs={10} sm={10} className="text-start">
          <Row>
            <Col xs={2} sm={1} className="p-0 ps-2">
              <img
                src={viewProfile}
                alt="eger"
                className=" col w-100 rounded-circle"
              />
            </Col>
            <Col xs={10} sm={10} className="">
              <Row>
                <Col sm={12}>
                  <p className="my-auto fw-bold">
                    {props.postCompany === 1 ? (
                      <a
                        href={postCompanyLink}
                        target="_blank"
                        rel="noreferrer"
                        className="text-decoration-none text-dark"
                      >
                        {" "}
                        {props.postCompanyName}
                      </a>
                    ) : (
                      <a
                        href="/home"
                        className="text-decoration-none text-dark"
                      >
                        {" "}
                        {props.postUserName}
                      </a>
                    )}
                  </p>
                </Col>
                <Col sm={12}>
                  <p className="text-muted fw-light fs-7">
                    <BsClockFill size={12} className="me-1" />
                    {/* 10th June 2021 5:14 pm */}
                    {props.postTime}
                  </p>
                </Col>
              </Row>
            </Col>
          </Row>
        </Col>
        <Col xs={2} sm={2} className="text-end">
          <div className="dropdown">
            <button
              className="btn shadow-none"
              type="button"
              id="dropdownMenuButton1"
              data-bs-toggle="dropdown"
              aria-expanded="false"
            >
              <BsThreeDotsVertical />
            </button>
            <ul
              className="dropdown-menu dropdown-menu-end"
              aria-labelledby="dropdownMenuButton1"
            >
            {props.postOwnerID === userID && <> 
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  onClick={test}
                >
                  <BsPencilSquare className="icon" />
                  Edit
                </button>
              </li>
              <li>
                <button
                  type="button"
                  className="btn btn-link dropdown-item"
                  value={props.postID}
                  onClick={deletePost}
                >
                  <BsTrash className="icon" /> Delete
                </button>
              </li>
              <li>
                <hr className="dropdown-divider" />
              </li>
              </>}
              <li>
                <Report />
              </li>
            </ul>
          </div>
        </Col>
      </Row>
      <Row className="mb-3">
        <Col sm={12} className="px-3">

          {props.postText !== "" && (
            <div
            dangerouslySetInnerHTML={{
              __html: props.postText.replace(/href/g, "target='_blank' href"),
            }}
          />
          )}
          {props.postImageName !== "" && (
            <img src={props.postImage} alt="rvr" className="w-100 mb-2" />
          )}
          {props.postVideoName !== "" && (
            <video controls className="d-flex w-75 mx-auto">
              <source
