import React, { useState, useEffect,Fragment } from 'react';
import { useNavigate } from "react-router-dom";
import Axios from "axios";
import { BiPencil, BiMinusCircle } from "react-icons/bi";

import { Col, Row, Modal, Button, Form } from "react-bootstrap";
import Heading from '../../../Heading';
import { toast } from "react-toastify";



function About() {
  const navigate = useNavigate();


  const [about, setAbout] = useState([]);
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (!localStorage.getItem("accessToken")) {
      navigate("/signup");
    } else {
    Axios.get(`${process.env.REACT_APP_SERVER_DOMAIN}/dashboardUserProfile/about/`,
    {
      headers: {
        accessToken: localStorage.getItem("accessToken"),
      },
    }
    ).then(
      (response) => {
        if (response.data.error) {
            return toast.error(
              <Fragment>
                <BiMinusCircle /> <span>{response.data.error}</span>
              </Fragment>
            );
          } else {
            setAbout(response.data);
          }

      }
    );
  }
  }, []);

    return (
      <>
        <Row className="mx-auto border rounded p-2 my-2">
        <Col sm={12}>
         <Row>
         <Col xs={10} sm={10} className="text-start">
            <Heading content="About Me" design="h5" />
          </Col>
          <Col xs={2} sm={2} className="text-end align-self-center">
            <p role="button" className="my-auto" onClick={() => setShow(true)}><BiPencil className="icon" /></p>
          </Col>
         </Row>
        </Col>
        <Col sm={12}>
          <Col> 
          {about.map((aboutDetails, key) => {
                const aboutUser = aboutDetails.about_user;

                return (
                    <p className="fs-7 text-truncate-3 text-justify">
                    {aboutUser}
                    </p>
                )
            })}
          </Col>
        </Col>
      </Row>
      <Modal centered show={show} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title><Heading content="About Me" design="h4 my-auto" /></Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
          <Col sm={12} className="form-group mb-3">
                    <label htmlFor="studyField" className="required">
                      Personal Name
                    </label>
                    <input
                      type="text"
                      placeholder="Enter the person-in-charge name"
                      // className={nameClasses}
                      // onChange={nameChangeHandler}
                      // onBlur={nameBlurHandler}
                      // value={enteredName}
                    />

                    {/* {nameInputHasError && (
                      <Form.Control.Feedback type="invalid">
                        Please fill in person-in-charge name.
                      </Form.Control.Feedback>
                    )} */}
                  </Col>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShow(false)}>
            Close
          </Button>
          <Button variant="primary" onClick={() => setShow(false)}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
      </>
    )
}

export default About
