import React, { Fragment, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { BiError, BiMessageCheck, BiMinusCircle } from "react-icons/bi";

//Toast
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Verify() {
    
    const { token } = useParams();

    useEffect(() => {
        if (token) {
            Axios.get('http://localhost:3001/verify/token', {
                token: token
            })
                .then((response) => {
                    if (response.data.success) {
                        return toast.success(
                            <Fragment>
                                <BiMessageCheck />
                                <span>{response.data.success}</span>
                            </Fragment>
    
                        );
                    } else if (response.data.warn) {
                        return toast.warn(
                            <Fragment>
                                <BiError />
                                <span>{response.data.warn}</span>
                            </Fragment>);
                    }  else {
                        return toast.error(
                            <Fragment>
                                <BiMinusCircle />
                                <span>{response.data.error}</span>
                            </Fragment>);
                    }
                }
                );
        }
        else {
            return toast.error(
                <Fragment>
                    <BiMinusCircle />
                    <span>System is Error. Please Try Again!</span>
                </Fragment>);
        }
        if (token === null) {
            window.location.href = '/index';
        }
        
    }, [token]);
 





    return (
        <Fragment>
            <ToastContainer
                position="top-center"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                render="/index"
            />
        </Fragment>
    );


}

export default Verify;
